import{_ as e,o,c as a,a as d}from"./app.6afbe0de.js";const p={},s=d('<h1 id="\u4E3A\u4EC0\u4E48\u6211\u4EEC\u9009\u62E9\u4E86node-js\u800C\u4E0D\u662Fdeno" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u6211\u4EEC\u9009\u62E9\u4E86node-js\u800C\u4E0D\u662Fdeno" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u6211\u4EEC\u9009\u62E9\u4E86Node.js\u800C\u4E0D\u662FDeno\uFF1F</h1><p>\u81EA\u4ECE2009\u5E74\u5F15\u5165Node.js\u4EE5\u6765\uFF0C\u5B83\u8FC5\u901F\u83B7\u5F97\u4E86\u5F3A\u52B2\u7684\u52BF\u5934\u548C\u5E7F\u6CDB\u7684\u5E94\u7528\uFF0C\u6210\u4E3A2010\u5E74\u4EE3\u7F16\u5199\u670D\u52A1\u5668\u7AEFJavaScript\u5E94\u7528\u7A0B\u5E8F\u7684\u4E8B\u5B9E\u6807\u51C6\u3002\u7136\u540E\uFF0C\u5F53Node.js\u7684\u521B\u5EFA\u8005Ryan Dahl\u57282018\u5E74\u7684JSConf EU\u6F14\u8BB2\u4E2D\u4ECB\u7ECD\u4E86Deno\uFF0C\u5E76\u4EE5&quot;\u5173\u4E8ENode.js\u6211\u540E\u6094\u768410\u4EF6\u4E8B&quot;\u4E3A\u9898\uFF0C\u4F3C\u4E4ENode.js\u7684\u7EDF\u6CBB\u5730\u4F4D\u53D7\u5230\u5A01\u80C1\u3002</p><p>\u5F53\u6211\u4EEC\u57282020\u5E74\u5F00\u59CB\u7740\u773C\u4E8EJavaScript\u540E\u7AEF\u5F00\u53D1\u65F6\uFF08\u5B9E\u9645\u4E0A\u662FTypeScript\uFF0C\u4F46\u8FD9\u662F\u53E6\u4E00\u7BC7\u535A\u5BA2\u6587\u7AE0\u7684\u6545\u4E8B\uFF09\uFF0C\u6211\u4EEC\u5FC5\u987B\u505A\u51FA\u9009\u62E9 - \u662F\u9009\u62E9\u8001\u5C06Node.js\uFF0C\u8FD8\u662F\u9009\u62E9\u65B0\u5174\u3001\u95EA\u4EAE\u5E76\u5FEB\u901F\u589E\u957F\u7684Deno\u3002</p><p>\u6211\u4EEC\u7684\u51B3\u5B9A\u53EF\u80FD\u4F1A\u8BA9\u4E00\u4E9B\u4EBA\u611F\u5230\u60CA\u8BB6\uFF0C\u4F46\u5BF9\u6211\u4EEC\u6765\u8BF4\uFF0C\u9009\u62E9Node.js\u800C\u4E0D\u662FDeno\u662F\u5F88\u5BB9\u6613\u7684\u3002\u800C\u4E14\uFF0C\u5373\u4F7F\u56DE\u5230\u4ECA\u5929\uFF0C\u6211\u4EEC\u4ECD\u7136\u4F1A\u505A\u51FA\u540C\u6837\u7684\u9009\u62E9\uFF01</p><p>\u672C\u6587\u6982\u8FF0\u4E86\u6BCF\u4E2A\u8FD0\u884C\u65F6\u73AF\u5883\u63D0\u4F9B\u7684\u5E73\u53F0\u3001\u5386\u53F2\u3001\u6027\u80FD\u3001\u5B89\u5168\u6027\u4EE5\u53CA\u5176\u4ED6\u5173\u952E\u529F\u80FD\uFF0C\u5E76\u5C55\u793A\u4E86Node.js\u4ECD\u7136\u662FJavaScript/TypeScript\u540E\u7AEF\u8FD0\u884C\u65F6\u73AF\u5883\u7684\u738B\u8005\u3002</p><h2 id="node-js\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#node-js\u662F\u4EC0\u4E48" aria-hidden="true">#</a> Node.js\u662F\u4EC0\u4E48</h2><p>Node.js\u662F\u5EFA\u7ACB\u5728Chrome\u7684V8\u5F15\u64CE\u4E4B\u4E0A\u7684\u670D\u52A1\u5668\u7AEFJavaScript\u8FD0\u884C\u65F6\u73AF\u5883\uFF0C\u7531Ryan Dahl\u4E8E2009\u5E74\u63A8\u51FA\uFF0C\u5E76\u6210\u4E3A\u6D41\u884C\u7684\u670D\u52A1\u5668\u7AEFJavaScript\u8FD0\u884C\u65F6\u73AF\u5883\u4E4B\u4E00\u3002\u7531\u4E8E\u5176\u53EF\u6269\u5C55\u6027\u3001\u6027\u80FD\u548C\u5E9E\u5927\u7684\u5E93\u548C\u6846\u67B6\u751F\u6001\u7CFB\u7EDF\uFF0C\u5F00\u53D1\u8005\u5E7F\u6CDB\u91C7\u7528\u4E86Node.js\u3002\u5927\u591A\u6570\u516C\u53F8\u9009\u62E9Node.js\u4F5C\u4E3A\u670D\u52A1\u5668\u7AEF\u8FD0\u884C\u65F6\u73AF\u5883\uFF0C\u56E0\u4E3AJavaScript\u4E5F\u53EF\u4EE5\u7528\u4F5C\u6784\u5EFA\u5BA2\u6237\u7AEF\u5E94\u7528\u7A0B\u5E8F\u7684\u811A\u672C\u8BED\u8A00\u3002</p><p>\u622A\u81F32023\u5E74\uFF0CNode.js\u5DF2\u7ECF\u5F97\u5230\u4E86\u5F88\u597D\u7684\u53D1\u5C55\uFF0C\u5E76\u6210\u4E3A\u6784\u5EFA\u670D\u52A1\u5668\u7AEF\u5E94\u7528\u7A0B\u5E8F\u7684\u53D7\u6B22\u8FCE\u548C\u5E7F\u6CDB\u4F7F\u7528\u7684JavaScript\u8FD0\u884C\u65F6\u73AF\u5883\u3002\u8BB8\u591A\u516C\u53F8\u4F7F\u7528Node.js\uFF0C\u5E76\u5728\u4E92\u8054\u7F51\u4E0A\u4EAB\u6709\u5F3A\u5927\u7684\u793E\u533A\u652F\u6301\u3002\u57282023\u5E744\u6708\uFF0C\u53D1\u5E03\u4E86\u6700\u65B0\u7684Node.js\u7248\u672C20\u3002Node.js 20\u7684\u6700\u91CD\u8981\u66F4\u65B0\u662F\u5F15\u5165\u4E86\u4E00\u4E2A\u9075\u5FAADeno\u7684\u6743\u9650\u6A21\u578B\uFF0C\u7528\u6237\u53EF\u4EE5\u660E\u786E\u6307\u5B9A\u8FD0\u884C\u65F6\u6743\u9650\uFF0C\u5982\u6587\u4EF6\u7CFB\u7EDF\u8BBF\u95EE\u6216\u9650\u5236\u5DE5\u4F5C\u7EBF\u7A0B\u3002Node.js\u9ED8\u8BA4\u6346\u7ED1\u4E86\u7A33\u5B9A\u7248\u672C\u7684WebCrpto API\u3001HTTP/1.1 KeepAlive\u7B49\u8BB8\u591A\u663E\u8457\u66F4\u65B0\u3002</p><h3 id="node-js\u7684\u4F18\u52BF\u548C\u52A3\u52BF" tabindex="-1"><a class="header-anchor" href="#node-js\u7684\u4F18\u52BF\u548C\u52A3\u52BF" aria-hidden="true">#</a> Node.js\u7684\u4F18\u52BF\u548C\u52A3\u52BF</h3><p>Node.js\u7684\u663E\u7740\u4F18\u52BF\u5305\u62EC\uFF1A</p><p>\u5FEB\u901F\u548C\u53EF\u6269\u5C55\uFF1ANode.js\u4F7F\u7528\u4E8B\u4EF6\u9A71\u52A8\u3001\u975E\u963B\u585E\u7684I/O\u6A21\u578B\uFF0C\u4F7F\u5176\u5177\u6709\u9AD8\u6027\u80FD\u548C\u5904\u7406\u8BB8\u591A\u5E76\u53D1\u8BF7\u6C42\u7684\u80FD\u529B\u3002</p><p>\u5E9E\u5927\u7684\u751F\u6001\u7CFB\u7EDF\uFF1ANode.js\u62E5\u6709\u5E7F\u6CDB\u6210\u719F\u7684\u751F\u6001\u7CFB\u7EDF\uFF0C\u63D0\u4F9B\u4E86\u8BB8\u591A\u5E93\u3001\u6846\u67B6</p><p>\u5982Express.js\u548CNest.js\uFF0C\u53EF\u4EE5\u5FEB\u901F\u6784\u5EFAWeb\u5E94\u7528\u7A0B\u5E8F\u548CAPI\u3002</p><p>\u793E\u533A\u548C\u652F\u6301\uFF1ANode.js\u62E5\u6709\u5145\u6EE1\u6D3B\u529B\u548C\u6D3B\u8DC3\u7684\u793E\u533A\uFF0C\u63D0\u4F9B\u4E86\u8BB8\u591A\u5728\u7EBF\u8D44\u6E90\u3001\u6559\u7A0B\u548C\u8BBA\u575B\uFF0C\u53EF\u7528\u4E8E\u5E2E\u52A9\u548C\u652F\u6301\u3002\u793E\u533A\u79EF\u6781\u8D21\u732E\u4E8E\u65B0\u5DE5\u5177\u548C\u5E93\u7684\u5F00\u53D1\uFF0C\u786E\u4FDD\u751F\u6001\u7CFB\u7EDF\u7684\u589E\u957F\u3002</p><p>\u53EF\u8FD0\u884C\u4E8E\u4EFB\u4F55\u5730\u65B9\uFF1ANode.js\u652F\u6301\u4E0EWindows\u3001Linux\u548CmacOS\u7684\u8DE8\u5E73\u53F0\u517C\u5BB9\u6027\u3002</p><p>NPM\u548C\u5305\u7BA1\u7406\uFF1ANode.js\u5229\u7528NPM\uFF08Node Package Manager\uFF09\uFF0C\u8FD9\u662F\u8F6F\u4EF6\u5F00\u53D1\u4E2D\u6700\u8457\u540D\u7684\u5305\u6CE8\u518C\u8868\u4E4B\u4E00\u3002NPM\u63D0\u4F9B\u4E86\u6210\u5343\u4E0A\u4E07\u4E2A\u5F00\u6E90\u5305\u7684\u8BBF\u95EE\uFF0C\u53EF\u4EE5\u8F7B\u677E\u5730\u5C06\u7B2C\u4E09\u65B9\u4EE3\u7801\u96C6\u6210\u5230\u9879\u76EE\u4E2D\u3002</p><p>\u589E\u5F3A\u7684\u5B89\u5168\u63A7\u5236\uFF1ANode.js\u7248\u672C20\u5F15\u5165\u4E86\u6539\u8FDB\u7684\u5B89\u5168\u63A7\u5236\uFF0C\u5305\u62EC\u52A0\u5F3A\u7684\u9ED8\u8BA4\u914D\u7F6E\u548C\u66F4\u4E25\u683C\u7684\u6743\u9650\u548C\u8BBF\u95EE\u63A7\u5236\u3002\u8FD9\u4E9B\u589E\u5F3A\u529F\u80FD\u63D0\u4F9B\u4E86\u66F4\u9AD8\u7684\u5E94\u7528\u7A0B\u5E8F\u5B89\u5168\u6027\u548C\u5F39\u6027\uFF0C\u4E3A\u5F00\u53D1\u8005\u63D0\u4F9B\u4E86\u66F4\u591A\u4FDD\u62A4\uFF0C\u4EE5\u9632\u6B62\u6F0F\u6D1E\u548C\u6076\u610F\u653B\u51FB\u3002</p><p>\u5FEB\u901F\u5F00\u53D1\uFF1A\u50CFAmplication\u8FD9\u6837\u7684\u5DE5\u5177\u4F7F\u5728Node.js\u4E0A\u4F7F\u7528\u548C\u5F00\u53D1\u5E94\u7528\u7A0B\u5E8F\u53D8\u5F97\u66F4\u52A0\u5BB9\u6613\uFF0C\u7701\u53BB\u4E86\u5927\u91CF\u7684\u5DE5\u4F5C\u548C\u65F6\u95F4\u3002</p><p>\u800C\u4F7F\u7528Node.js\u7684\u4E00\u4E9B\u7F3A\u70B9\u5305\u62EC\uFF1A</p><p>\u5355\u7EBF\u7A0B\uFF1ANode.js\u5728\u5355\u7EBF\u7A0B\u4E0A\u8FD0\u884C\uFF0C\u56E0\u6B64\u5BF9\u4E8E\u9700\u8981\u5927\u91CF\u5E76\u884C\u5904\u7406\u7684\u8BA1\u7B97\u5BC6\u96C6\u578B\u4EFB\u52A1\u53EF\u80FD\u4E0D\u5408\u9002\u3002\u957F\u65F6\u95F4\u8FD0\u884C\u7684CPU\u7ED1\u5B9A\u4EFB\u52A1\u53EF\u80FD\u4F1A\u963B\u585E\u4E8B\u4EF6\u5FAA\u73AF\uFF0C\u5BF9\u5E94\u7528\u7A0B\u5E8F\u7684\u54CD\u5E94\u6027\u4EA7\u751F\u8D1F\u9762\u5F71\u54CD\u3002</p><p>\u56DE\u8C03\u5730\u72F1\uFF1A\u5728\u590D\u6742\u7684Node.js\u5E94\u7528\u7A0B\u5E8F\u4E2D\uFF0C\u5D4C\u5957\u56DE\u8C03\u53EF\u80FD\u5BFC\u81F4\u56DE\u8C03\u5730\u72F1\uFF0C\u4F7F\u4EE3\u7801\u66F4\u96BE\u4EE5\u9605\u8BFB\u3001\u7EF4\u62A4\u548C\u8C03\u8BD5\u3002\u867D\u7136\u53EF\u4EE5\u901A\u8FC7\u4F7F\u7528Promises\u3001async/await\u6216\u91C7\u7528\u66F4\u73B0\u4EE3\u7684JavaScript\u7279\u6027\u6765\u7F13\u89E3\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u67D0\u4E9B\u6A21\u5757\u7684\u4E0D\u6210\u719F\uFF1A\u5C3D\u7BA1Node.js\u751F\u6001\u7CFB\u7EDF\u5E9E\u5927\uFF0C\u4F46\u67D0\u4E9B\u6A21\u5757\u53EF\u80FD\u5728\u7A33\u5B9A\u6027\u3001\u6587\u6863\u6216\u793E\u533A\u652F\u6301\u65B9\u9762\u4E0D\u591F\u6210\u719F\u3002\u4E3A\u4E86\u907F\u514D\u6F5C\u5728\u7684\u95EE\u9898\uFF0C\u91CD\u8981\u7684\u662F\u4ED4\u7EC6\u8BC4\u4F30\u6240\u9009\u62E9\u6A21\u5757\u7684\u8D28\u91CF\u548C\u53D7\u6B22\u8FCE\u7A0B\u5EA6\u3002</p><p>\u603B\u7ED3\u8D77\u6765\uFF0C\u7531\u4E8E\u5176\u51FA\u8272\u7684\u901F\u5EA6\u3001\u53EF\u6269\u5C55\u6027\u548CJavaScript\u7684\u666E\u53CA\u6027\uFF0CNode.js\u662F\u6784\u5EFAWeb\u5E94\u7528\u7A0B\u5E8F\u548CAPI\u7684\u4F18\u79C0\u9009\u62E9\u3002\u501F\u52A9NPM\u63D0\u4F9B\u7684\u5E7F\u6CDB\u5E93\u3001\u6846\u67B6\u548C\u5DE5\u5177\uFF0C\u5F00\u53D1\u8005\u53EF\u4EE5\u5FEB\u901F\u521B\u5EFA\u5065\u58EE\u7684\u5E94\u7528\u7A0B\u5E8F\u3002\u56F4\u7ED5Node.js\u7684\u79EF\u6781\u652F\u6301\u548C\u652F\u6301\u793E\u533A\u4E3A\u5B66\u4E60\u548C\u89E3\u51B3\u95EE\u9898\u63D0\u4F9B\u4E86\u4E30\u5BCC\u7684\u8D44\u6E90\u3002\u867D\u7136\u5B58\u5728\u4E00\u4E9B\u9650\u5236\uFF0C\u6BD4\u5982\u5355\u7EBF\u7A0B\uFF0C\u6267\u884C\u548C\u6F5C\u5728\u7684\u56DE\u8C03\u590D\u6742\u6027\uFF0C\u4F46\u5F00\u53D1\u8005\u53EF\u4EE5\u901A\u8FC7\u4F7F\u7528\u73B0\u4EE3\u7684JavaScript\u7279\u6027\u548C\u67B6\u6784\u6A21\u5F0F\u6765\u51CF\u8F7B\u8FD9\u4E9B\u9650\u5236\u3002\u603B\u4F53\u800C\u8A00\uFF0CNode.js\u4F7F\u5F00\u53D1\u8005\u80FD\u591F\u9AD8\u6548\u5730\u521B\u5EFA\u9AD8\u6027\u80FD\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u6210\u4E3A\u73B0\u4EE3Web\u5F00\u53D1\u7684\u4E00\u4E2A\u5F15\u4EBA\u6CE8\u76EE\u7684\u9009\u62E9\u3002</p><h2 id="\u90A3\u4E48-\u4EC0\u4E48\u662Fdeno" tabindex="-1"><a class="header-anchor" href="#\u90A3\u4E48-\u4EC0\u4E48\u662Fdeno" aria-hidden="true">#</a> \u90A3\u4E48\uFF0C\u4EC0\u4E48\u662FDeno\uFF1F</h2><p>Deno\u662F\u4E00\u79CD\u8F83\u65B0\u7684JavaScript\u8FD0\u884C\u65F6\uFF0C\u7531Node.js\u7684\u521B\u9020\u8005Ryan Dahl\u57282018\u5E74\u63A8\u51FA\u3002Deno\u65E8\u5728\u89E3\u51B3Node.js\u7684\u4E00\u4E9B\u95EE\u9898\uFF0C\u5305\u62EC\u5B89\u5168\u6027\u548C\u5BF9\u73B0\u4EE3JavaScript\u7279\u6027\u7684\u5185\u7F6E\u652F\u6301\u7684\u7F3A\u4E4F\u3002Deno\u662F\u57FA\u4E8ERust\u7F16\u7A0B\u8BED\u8A00\uFF08\u4F7F\u7528Tokio\u8FD0\u884C\u65F6\uFF09\u6784\u5EFA\u7684\uFF0C\u4E13\u6CE8\u4E8E\u9ED8\u8BA4\u7684\u5B89\u5168\u63AA\u65BD\u3002Deno\u5305\u62ECNode.js\u7F3A\u5C11\u7684\u51E0\u4E2A\u7EC4\u4EF6\uFF0C\u4F8B\u5982\u5185\u7F6E\u6A21\u5757\u7CFB\u7EDF\u3001\u5BF9TypeScript\u7684\u5F00\u7BB1\u5373\u7528\u652F\u6301\u4EE5\u53CA\u76F4\u63A5\u4ECEURL\u5BFC\u5165\u6A21\u5757\u7684\u80FD\u529B\u3002</p><p>\u622A\u81F32023\u5E74\uFF0CDeno\u5DF2\u7ECF\u83B7\u5F97\u4E86\u663E\u8457\u7684\u5173\u6CE8\uFF0C\u5E76\u6210\u4E3A\u5F00\u53D1\u8005\u7684\u4E00\u4E2A\u53D7\u6B22\u8FCE\u7684\u66FF\u4EE3\u9009\u62E9\uFF0C\u5C3D\u7BA1Node.js\u4ECD\u7136\u662F\u6784\u5EFA\u670D\u52A1\u5668\u7AEF\u5E94\u7528\u7A0B\u5E8F\u7684\u4E3B\u5BFC\u73A9\u5BB6\u3002Deno\u8FC5\u901F\u5438\u5F15\u4E86\u5F00\u53D1\u8005\u7684\u6CE8\u610F\uFF0C\u5E76\u5728\u8FCE\u5934\u8D76\u4E0A\u3002\u5B83\u7684\u6A21\u5757\u7CFB\u7EDF\u5141\u8BB8\u4F9D\u8D56\u7BA1\u7406\u548C\u7248\u672C\u63A7\u5236\uFF0C\u5BF9\u4E8E\u5927\u89C4\u6A21\u9879\u76EE\u6765\u8BF4\u662F\u4E00\u4E2A\u4F18\u52BF\u3002</p><h3 id="\u4F7F\u7528deno\u7684\u4E00\u4E9B\u4F18\u70B9\u5305\u62EC" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528deno\u7684\u4E00\u4E9B\u4F18\u70B9\u5305\u62EC" aria-hidden="true">#</a> \u4F7F\u7528Deno\u7684\u4E00\u4E9B\u4F18\u70B9\u5305\u62EC\uFF1A</h3><p>\u5B89\u5168\u6027\uFF1ADeno\u7684\u5B89\u5168\u6A21\u578B\u662F\u76F8\u5BF9\u4E8ENode.js\u7684\u4E00\u4E2A\u91CD\u5927\u4F18\u52BF\u3002Deno\u7684\u9ED8\u8BA4\u5B89\u5168\u8BBE\u7F6E\u786E\u4FDD\u4EE3\u7801\u5728\u4E00\u4E2A\u53D7\u9650\u7684\u73AF\u5883\u4E2D\u8FD0\u884C\uFF0C\u5F00\u53D1\u8005\u5FC5\u987B\u660E\u786E\u6388\u4E88\u6743\u9650\uFF0C\u9632\u6B62\u4E86\u8BB8\u591A\u4E4B\u524D\u5728Node.js\u4E2D\u5B58\u5728\u7684\u5B89\u5168\u95EE\u9898\u3002</p><p>\u539F\u751F\u652F\u6301TypeScript\uFF1ADeno\u5185\u7F6E\u652F\u6301TypeScript\uFF1BNode.js\u5728\u652F\u6301TypeScript\u65B9\u9762\u82B1\u8D39\u4E86\u5F88\u957F\u65F6\u95F4\uFF0C\u5E76\u4E14\u4ECD\u7136\u9700\u8981\u989D\u5916\u7684\u914D\u7F6E\u548C\u8BBE\u7F6E\u3002Deno\u4ECE\u4E00\u5F00\u59CB\u5C31\u660E\u786E\u652F\u6301TypeScript\uFF0C\u5BF9\u4E8E\u504F\u597D\u9759\u6001\u7C7B\u578B\u7684\u5F00\u53D1\u8005\u6765\u8BF4\u662F\u4E00\u4E2A\u4E0D\u9519\u7684\u9009\u62E9\u3002</p><p>\u73B0\u4EE3\u6A21\u5757\u7CFB\u7EDF\uFF1ADeno\u7684\u6A21\u5757\u7CFB\u7EDF\u5229\u7528ES\u6A21\u5757\uFF0C\u5141\u8BB8\u5F00\u53D1\u8005\u76F4\u63A5\u4ECEURL\u6216\u672C\u5730\u6587\u4EF6\u8DEF\u5F84\u5BFC\u5165\u6A21\u5757\u3002\u8FD9\u79CD\u73B0\u4EE3\u6A21\u5757\u7CFB\u7EDF\u4E0EWeb\u5E73\u53F0\u66F4\u597D\u5730\u517C\u5BB9\uFF0C\u5B83\u6D88\u9664\u4E86\u72EC\u7ACB\u7684\u5305\u7BA1\u7406\u5668\u7684\u9700\u8981\uFF0C\u7B80\u5316\u4E86\u4F9D\u8D56\u7BA1\u7406\uFF0C\u5E76\u5B9E\u73B0\u4E86\u66F4\u5BB9\u6613\u7684\u4EE3\u7801\u5171\u4EAB\u548C\u91CD\u7528\u3002</p><h3 id="\u800C\u4F7F\u7528deno\u7684\u4E00\u4E9B\u7F3A\u70B9\u5305\u62EC" tabindex="-1"><a class="header-anchor" href="#\u800C\u4F7F\u7528deno\u7684\u4E00\u4E9B\u7F3A\u70B9\u5305\u62EC" aria-hidden="true">#</a> \u800C\u4F7F\u7528Deno\u7684\u4E00\u4E9B\u7F3A\u70B9\u5305\u62EC\uFF1A</h3><p>\u6709\u9650\u7684\u751F\u6001\u7CFB\u7EDF\uFF1A\u4E0ENode.js\u76F8\u6BD4\uFF0CDeno\u62E5\u6709\u76F8\u5BF9\u8F83\u5C0F\u7684\u5E93\u3001\u6846\u67B6\u548C\u5DE5\u5177\u751F\u6001\u7CFB\u7EDF\u3002\u5C3D\u7BA1\u5B83\u6B63\u5728\u589E\u957F\uFF0C\u4F46\u751F\u6001\u7CFB\u7EDF\u53EF\u80FD\u4E0D\u591F\u5E7F\u6CDB\uFF0C\u9650\u5236\u4E86\u5F00\u53D1\u8005\u7684\u9009\u62E9\u548C\u8D44\u6E90\u7684\u53EF\u7528\u6027\u3002</p><p>\u7ECF\u9A8C\u4E0D\u8DB3\uFF1A\u7531\u4E8EDeno\u8F83\u65B0\u4E8E\u6280\u672F\u9886\u57DF\uFF0C\u4F7F\u7528\u5B83\u7684\u9879\u76EE\u660E\u663E\u8F83\u5C11\u3002\u5B83\u7684\u8F83\u4F4E\u4F7F\u7528\u7387\u610F\u5473\u7740\u4E00\u4E9B\u9519\u8BEF\u548C\u95EE\u9898\u53EF\u80FD\u4ECD\u9700\u8981\u89E3\u51B3\uFF0C\u6574\u4F53\u5E73\u53F0\u7684\u6210\u719F\u5EA6\u4E5F\u8F83\u4F4E\u4E8ENode.js\u3002</p><p>\u793E\u533A\u548C\u652F\u6301\uFF1A\u867D\u7136Deno\u6709\u4E00\u4E2A\u6D3B\u8DC3\u4E14\u4E0D\u65AD\u589E\u957F\u7684\u793E\u533A\uFF0C\u4F46\u5B83\u6CA1\u6709Node.js\u90A3\u6837\u5E7F\u6CDB\u7684\u793E\u533A\u3002\u8FD9\u79CD\u793E\u533A\u7684\u4E0D\u8DB3\u53EF\u80FD\u5BFC\u81F4\u8D44\u6E90\u3001\u6559\u7A0B\u548C\u793E\u533A\u652F\u6301\u76F8\u5BF9\u8F83\u5C11\uFF0C\u800C\u4E0D\u5982\u5E9E\u5927\u800C\u6210\u719F\u7684Node.js\u793E\u533A\u3002</p><p>\u4E0E\u73B0\u6709Node.js\u4EE3\u7801\u7684\u517C\u5BB9\u6027\uFF1ADeno\u91C7\u7528\u4E86ECMAScript\u6A21\u5757\u7684\u65B9\u5F0F\uFF0C\u8FD9\u53EF\u80FD\u5BFC\u81F4\u4E0E\u4F9D\u8D56\u4E8ECommonJS\u6A21\u5757\u7CFB\u7EDF\u7684\u73B0\u6709Node.js\u4EE3\u7801\u5E93\u5B58\u5728\u517C\u5BB9\u6027\u95EE\u9898\u3002\u5F53\u8FC1\u79FB\u6216\u96C6\u6210\u73B0\u6709\u9879\u76EE\u5230Deno\u65F6\uFF0C\u53EF\u80FD\u9700\u8981\u989D\u5916\u7684\u5DE5\u4F5C\u548C\u6F5C\u5728\u7684\u91CD\u6784\u3002</p><p>\u6709\u9650\u7684\u591A\u6838\u5229\u7528\uFF1ADeno\u4E0ENode.js\u7C7B\u4F3C\uFF0C\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u5728\u5355\u4E2A\u7EBF\u7A0B\u4E0A\u8FD0\u884C\uFF0C\u9650\u5236\u4E86\u5B83\u5145\u5206\u5229\u7528\u591A\u6838\u5904\u7406\u5668\u7684\u80FD\u529B\u3002\u867D\u7136Deno\u63D0\u4F9B\u4E86\u7C7B\u4F3CWorkers\u7684\u529F\u80FD\u6765\u5B9E\u73B0\u5E76\u53D1\u6267\u884C\uFF0C\u4F46\u4F7F\u7528\u591A\u4E2A\u6838\u5FC3\u53EF\u80FD\u9700\u8981\u989D\u5916\u7684\u624B\u52A8\u5B9E\u73B0\u548C\u534F\u8C03\u3002</p><p>\u603B\u800C\u8A00\u4E4B\uFF0CNode.js\u548CDeno\u90FD\u662F\u6784\u5EFA\u670D\u52A1\u5668\u7AEFJavaScript\u5E94\u7528\u7A0B\u5E8F\u7684\u51FA\u8272\u6846\u67B6\u3002\u7136\u800C\uFF0C\u4E0EDeno\u76F8\u6BD4\uFF0C\u5927\u591A\u6570\u5F00\u53D1\u8005\u4ECD\u7136\u66F4\u503E\u5411\u4E8E\u9009\u62E9Node.js\u6765\u521B\u5EFA\u670D\u52A1\u5668\u7AEFJavaScript\u5E94\u7528\u7A0B\u5E8F\u3002Node.js\u51ED\u501F\u5176\u5E9E\u5927\u7684\u793E\u533A\u548C\u4F17\u591A\u516C\u53F8\u7684\u4F7F\u7528\uFF0C\u4E0D\u65AD\u6F14\u8FDB\u548C\u5F15\u5165\u65B0\u529F\u80FD\uFF1B\u5B83\u4ECD\u7136\u662F\u6784\u5EFA\u670D\u52A1\u5668\u7AEFJavaScript\u5E94\u7528\u7A0B\u5E8F\u7684\u7EC8\u6781\u6539\u53D8\u8005\u3002</p><p>\u4E13\u4E1A\u63D0\u793A\uFF1A\u5982\u679C\u4F60\u6B63\u5728\u6784\u5EFA\u73B0\u4EE3\u5316\u7684Node.js\u5E94\u7528\u7A0B\u5E8F\u548C\u670D\u52A1\uFF0C\u5E76\u4E14\u5E0C\u671B\u901A\u8FC7\u5FAE\u670D\u52A1\u7684\u65B9\u5F0F\u7B80\u5316\u5F00\u53D1\u548C\u7EF4\u62A4\u5E94\u7528\u7A0B\u5E8F\uFF0C\u53EF\u4EE5\u8003\u8651\u4F7F\u7528\u50CFAmplication\u8FD9\u6837\u7684\u5DE5\u5177\uFF0C\u4EE5\u63D0\u9AD8\u5F00\u53D1\u6548\u7387\u548C\u751F\u4EA7\u529B\uFF0C\u5B9E\u73B0\u66F4\u5FEB\u7684\u53D1\u5E03\u5468\u671F\uFF01</p>',38),n=[s];function r(i,t){return o(),a("div",null,n)}const N=e(p,[["render",r],["__file","12.html.vue"]]);export{N as default};
