import{_ as a,o as e,c as t,a as r}from"./app.211258b5.js";const c={},p=r('<h1 id="react-js-\u5F00\u53D1\u8005\u9762\u8BD5\u9898\u53CA\u7B54\u6848" tabindex="-1"><a class="header-anchor" href="#react-js-\u5F00\u53D1\u8005\u9762\u8BD5\u9898\u53CA\u7B54\u6848" aria-hidden="true">#</a> React JS \u5F00\u53D1\u8005\u9762\u8BD5\u9898\u53CA\u7B54\u6848</h1><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B\u4EC0\u4E48\u662F-react-\u4EE5\u53CA\u5B83\u4E0E\u5176\u4ED6-javascript-\u6846\u67B6\u7684\u533A\u522B\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B\u4EC0\u4E48\u662F-react-\u4EE5\u53CA\u5B83\u4E0E\u5176\u4ED6-javascript-\u6846\u67B6\u7684\u533A\u522B\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B\u4EC0\u4E48\u662F React \u4EE5\u53CA\u5B83\u4E0E\u5176\u4ED6 JavaScript \u6846\u67B6\u7684\u533A\u522B\u5417\uFF1F</h2><p>React \u662F\u4E00\u4E2A\u7528\u4E8E\u6784\u5EFA\u7528\u6237\u754C\u9762\u7684 JavaScript \u5E93\u3002\u5B83\u7684\u72EC\u7279\u4E4B\u5904\u5728\u4E8E\u5B83\u4F7F\u7528\u865A\u62DF DOM \u6765\u6709\u6548\u5730\u66F4\u65B0 UI\uFF0C\u4ECE\u800C\u53EF\u4EE5\u66F4\u5FEB\u3001\u66F4\u8F7B\u677E\u5730\u5904\u7406 UI \u7684\u66F4\u6539\u3002</p><p>React \u4E0D\u540C\u4E8E\u5176\u4ED6 JavaScript \u6846\u67B6\uFF0C\u56E0\u4E3A\u5B83\u53EA\u5173\u6CE8\u5E94\u7528\u7A0B\u5E8F\u7684\u89C6\u56FE\u5C42\uFF0C\u53EF\u4EE5\u66F4\u597D\u5730\u4E0E\u5176\u4ED6\u5E93\u548C\u6846\u67B6\u96C6\u6210\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u865A\u62DF-dom-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u865A\u62DF-dom-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u865A\u62DF DOM \u7684\u6982\u5FF5\u5417\uFF1F</h2><p>React \u4E2D\u7684\u865A\u62DF DOM \u662F\u5B9E\u9645 DOM \u7684\u8F7B\u91CF\u7EA7\u5185\u5B58\u8868\u793A\u3002React \u66F4\u65B0\u865A\u62DF DOM \u800C\u4E0D\u662F\u5B9E\u9645 DOM\uFF0C\u5E76\u4E14\u53EA\u66F4\u65B0 DOM \u4E2D\u53D1\u751F\u53D8\u5316\u7684\u90E8\u5206\uFF0C\u8FD9\u4F7F\u5F97\u5B83\u6BD4\u66F4\u65B0\u6574\u4E2A DOM \u7684\u5176\u4ED6\u5E93\u6216\u6846\u67B6\u8981\u5FEB\u5F97\u591A\u3002</p><p>\u901A\u8FC7\u4EC5\u66F4\u65B0\u5DF2\u66F4\u6539\u7684\u5185\u5BB9\uFF0CReact \u786E\u4FDD UI \u5BF9\u7528\u6237\u4FDD\u6301\u54CD\u5E94\u548C\u5FEB\u901F\uFF0C\u5373\u4F7F\u6709\u5927\u91CF\u66F4\u65B0\u3002</p><h2 id="react-\u7684\u4E3B\u8981\u7279\u70B9\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#react-\u7684\u4E3B\u8981\u7279\u70B9\u662F\u4EC0\u4E48" aria-hidden="true">#</a> React \u7684\u4E3B\u8981\u7279\u70B9\u662F\u4EC0\u4E48\uFF1F</h2><p>\u7EC4\u4EF6\uFF1A\u53EF\u4EE5\u7EC4\u5408\u4EE5\u6784\u5EFA\u590D\u6742 UI \u7684\u53EF\u91CD\u7528 UI \u5143\u7D20\u3002</p><p>\u865A\u62DF DOM\uFF1A\u5B9E\u9645 DOM \u7684\u865A\u62DF\u8868\u793A\uFF0C\u53EF\u63D0\u9AD8\u6027\u80FD\u3002</p><p>JSX\uFF1A\u4E00\u79CD\u8BED\u6CD5\u6269\u5C55\uFF0C\u5141\u8BB8\u60A8\u5728 JavaScript \u4E2D\u7F16\u5199\u7C7B\u4F3C HTML \u7684\u4EE3\u7801\u3002</p><p>\u5355\u5411\u6570\u636E\u6D41\uFF1A\u6570\u636E\u901A\u8FC7 props \u4ECE\u7236\u7EC4\u4EF6\u5411\u4E0B\u4F20\u9012\u5230\u5B50\u7EC4\u4EF6\u3002</p><p>\u670D\u52A1\u5668\u7AEF\u6E32\u67D3\uFF1AReact \u5141\u8BB8\u60A8\u5728\u670D\u52A1\u5668\u4E0A\u6E32\u67D3\u7EC4\u4EF6\uFF0C\u4ECE\u800C\u63D0\u9AD8\u6027\u80FD\u548C SEO\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u7EC4\u4EF6\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u7EC4\u4EF6\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u7EC4\u4EF6\u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u7EC4\u4EF6\u662F React \u5E94\u7528\u7A0B\u5E8F\u7684\u6784\u5EFA\u5757\u3002\u5B83\u4EEC\u662F\u53EF\u91CD\u7528\u7684 UI \u5143\u7D20\uFF0C\u53EF\u4EE5\u7EC4\u5408\u8D77\u6765\u6784\u5EFA\u590D\u6742\u7684 UI\u3002\u7EC4\u4EF6\u53EF\u4EE5\u6709\u81EA\u5DF1\u7684\u72B6\u6001\u3001\u9053\u5177\u548C\u751F\u547D\u5468\u671F\u65B9\u6CD5\uFF0C\u5E76\u4E14\u53EF\u4EE5\u4ECE\u7236\u7EC4\u4EF6\u4F20\u9012\u6570\u636E\u548C\u4E8B\u4EF6\u3002</p><p>\u6BCF\u4E2A\u7EC4\u4EF6\u90FD\u662F\u4E00\u4E2A\u72EC\u7ACB\u7684\u5355\u5143\uFF0C\u53EF\u4EE5\u8F7B\u677E\u7EF4\u62A4\u548C\u91CD\u7528\uFF0C\u4ECE\u800C\u53EF\u4EE5\u8F7B\u677E\u6784\u5EFA\u548C\u7BA1\u7406\u5927\u578B\u3001\u590D\u6742\u7684\u5E94\u7528\u7A0B\u5E8F\u3002</p><h2 id="react-\u4E2D\u7684-jsx-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#react-\u4E2D\u7684-jsx-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> React \u4E2D\u7684 JSX \u662F\u4EC0\u4E48\uFF1F</h2><p>JSX \u662F JavaScript \u7684\u8BED\u6CD5\u6269\u5C55\uFF0C\u5141\u8BB8\u60A8\u5728 JavaScript \u4E2D\u7F16\u5199\u7C7B\u4F3C HTML \u7684\u4EE3\u7801\u3002\u5728 React \u4E2D\uFF0CJSX \u7528\u4E8E\u5B9A\u4E49\u7EC4\u4EF6\u7684\u7ED3\u6784\u548C\u5916\u89C2\u3002</p><p>JSX \u4F7F\u7F16\u5199\u548C\u7406\u89E3 React \u7EC4\u4EF6\u7684\u7ED3\u6784\u53D8\u5F97\u66F4\u52A0\u5BB9\u6613\uFF0C\u56E0\u4E3A\u4EE3\u7801\u4E0E HTML \u975E\u5E38\u76F8\u4F3C\u3002\u5B83\u8FD8\u5141\u8BB8\u4E0E\u5176\u4ED6\u5DE5\u5177\u548C\u6280\u672F\u66F4\u597D\u5730\u96C6\u6210\uFF0C\u4F8B\u5982\u6784\u5EFA\u5DE5\u5177\u548C linters\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u7684\u5355\u5411\u6570\u636E\u6D41\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u7684\u5355\u5411\u6570\u636E\u6D41\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u7684\u5355\u5411\u6570\u636E\u6D41\u5417\uFF1F</h2><p>\u5728 React \u4E2D\uFF0C\u6570\u636E\u901A\u8FC7 props \u4ECE\u7236\u7EC4\u4EF6\u4F20\u9012\u5230\u5B50\u7EC4\u4EF6\u3002\u8FD9\u79CD\u5355\u5411\u6570\u636E\u6D41\u786E\u4FDD\u5B50\u7EC4\u4EF6\u65E0\u6CD5\u4FEE\u6539\u5B83\u4EEC\u7684\u5C5E\u6027\uFF0C\u4ECE\u800C\u66F4\u5BB9\u6613\u7406\u89E3\u548C\u8C03\u8BD5\u5E94\u7528\u7A0B\u5E8F\u3002</p><p>\u5355\u5411\u6570\u636E\u6D41\u8FD8\u53EF\u4EE5\u63D0\u4F9B\u66F4\u597D\u7684\u6027\u80FD\uFF0C\u56E0\u4E3A React \u53EF\u4EE5\u901A\u8FC7\u51C6\u786E\u4E86\u89E3\u54EA\u4E9B\u7EC4\u4EF6\u5C06\u53D7\u5230\u6570\u636E\u66F4\u6539\u7684\u5F71\u54CD\u6765\u4F18\u5316 UI \u7684\u66F4\u65B0\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u72B6\u6001\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u72B6\u6001\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u72B6\u6001\u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u5728 React \u4E2D\uFF0C\u72B6\u6001\u6307\u7684\u662F\u51B3\u5B9A\u7EC4\u4EF6\u884C\u4E3A\u5E76\u5411\u7528\u6237\u5448\u73B0\u4FE1\u606F\u7684\u6570\u636E\u6216\u53D8\u91CF\u3002\u4E0E props \u4E0D\u540C\uFF0C\u72B6\u6001\u53EF\u4EE5\u5728\u7EC4\u4EF6\u5185\u66F4\u6539\uFF0C\u5141\u8BB8\u7EC4\u4EF6\u8DDF\u8E2A\u7528\u6237\u4EA4\u4E92\u5E76\u76F8\u5E94\u5730\u66F4\u65B0 UI\u3002</p><p>\u4FDD\u6301\u72B6\u6001\u6700\u5C0F\u5316\u5E76\u4E14\u53EA\u5305\u542B\u7EC4\u4EF6\u5185\u53D1\u751F\u53D8\u5316\u7684\u6570\u636E\u5F88\u91CD\u8981\uFF0C\u56E0\u4E3A\u590D\u6742\u7684\u72B6\u6001\u4F1A\u4F7F\u5E94\u7528\u7A0B\u5E8F\u96BE\u4EE5\u7406\u89E3\u548C\u8C03\u8BD5\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F\u65B9\u6CD5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F\u65B9\u6CD5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u7EC4\u4EF6\u7684\u751F\u547D\u5468\u671F\u65B9\u6CD5\u5417\uFF1F</h2><p>React \u7EC4\u4EF6\u6709\u4E00\u4E2A\u751F\u547D\u5468\u671F\uFF0C\u5B83\u51B3\u5B9A\u4E86\u5B83\u4EEC\u4F55\u65F6\u88AB\u521B\u5EFA\u3001\u66F4\u65B0\u548C\u9500\u6BC1\u3002\u751F\u547D\u5468\u671F\u65B9\u6CD5\u662F\u5728\u7EC4\u4EF6\u751F\u547D\u5468\u671F\u7684\u7279\u5B9A\u70B9\u8C03\u7528\u7684\u65B9\u6CD5\uFF0C\u5B83\u4EEC\u5141\u8BB8\u60A8\u6267\u884C\u7279\u5B9A\u7684\u64CD\u4F5C\uFF0C\u4F8B\u5982\u83B7\u53D6\u6570\u636E\u6216\u66F4\u65B0 UI\u3002</p><ul><li>\u4E00\u4E9B\u5E38\u89C1\u7684\u751F\u547D\u5468\u671F\u65B9\u6CD5\u5305\u62EC\uFF1A <ul><li>componentDidMount\uFF1A\u5728\u7EC4\u4EF6\u6DFB\u52A0\u5230 DOM \u540E\u8C03\u7528\u3002</li><li>shouldComponentUpdate\uFF1A\u5728\u7EC4\u4EF6\u66F4\u65B0\u4E4B\u524D\u8C03\u7528\uFF0C\u8BA9\u4F60\u907F\u514D\u4E0D\u5FC5\u8981\u7684\u66F4\u65B0\u3002</li><li>componentDidUpdate\uFF1A\u5728\u7EC4\u4EF6\u66F4\u65B0\u540E\u8C03\u7528\u3002</li><li>componentWillUnmount\uFF1A\u5728\u7EC4\u4EF6\u4ECE DOM \u4E2D\u79FB\u9664\u4E4B\u524D\u8C03\u7528\u3002</li></ul></li></ul><p>\u901A\u8FC7\u4F7F\u7528\u751F\u547D\u5468\u671F\u65B9\u6CD5\uFF0C\u60A8\u53EF\u4EE5\u63A7\u5236\u7EC4\u4EF6\u66F4\u65B0\u7684\u65F6\u95F4\u548C\u65B9\u5F0F\uFF0C\u786E\u4FDD\u60A8\u7684\u5E94\u7528\u7A0B\u5E8F\u4FDD\u6301\u9AD8\u6027\u80FD\u548C\u54CD\u5E94\u80FD\u529B\u3002</p><h2 id="react-\u4E2D\u7684-props-\u548C-state-\u6709\u4EC0\u4E48\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#react-\u4E2D\u7684-props-\u548C-state-\u6709\u4EC0\u4E48\u533A\u522B" aria-hidden="true">#</a> React \u4E2D\u7684 props \u548C state \u6709\u4EC0\u4E48\u533A\u522B\uFF1F</h2><p>Props \u548C state \u90FD\u7528\u4E8E\u5728 React \u7EC4\u4EF6\u4E2D\u5B58\u50A8\u6570\u636E\uFF0C\u4F46\u5B83\u4EEC\u6709\u4E0D\u540C\u7684\u7528\u9014\u548C\u7528\u4F8B\u3002</p><p>Props\u4ECE\u7236\u7EC4\u4EF6\u5411\u4E0B\u4F20\u9012\u5230\u5B50\u7EC4\u4EF6\uFF0C\u5E76\u7528\u4E8E\u5C06\u6570\u636E\u4ECE\u4E00\u4E2A\u7EC4\u4EF6\u4F20\u9012\u5230\u53E6\u4E00\u4E2A\u7EC4\u4EF6\u3002Props\u662F\u53EA\u8BFB\u7684\uFF0C\u4E0D\u80FD\u5728\u5B50\u7EC4\u4EF6\u5185\u66F4\u6539\u3002</p><p>\u53E6\u4E00\u65B9\u9762\uFF0C\u72B6\u6001\u662F\u7EC4\u4EF6\u7684\u672C\u5730\u6570\u636E\uFF0C\u53EF\u4EE5\u5728\u7EC4\u4EF6\u5185\u66F4\u6539\u3002\u5B83\u7528\u4E8E\u8DDF\u8E2A\u66F4\u6539\u5E76\u6839\u636E\u7528\u6237\u4EA4\u4E92\u66F4\u65B0 UI\u3002</p><h2 id="\u4F60\u5982\u4F55\u5904\u7406-react-\u4E2D\u7684\u4E8B\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u4F60\u5982\u4F55\u5904\u7406-react-\u4E2D\u7684\u4E8B\u4EF6" aria-hidden="true">#</a> \u4F60\u5982\u4F55\u5904\u7406 React \u4E2D\u7684\u4E8B\u4EF6\uFF1F</h2><p>\u5728 React \u4E2D\uFF0C\u4E8B\u4EF6\u662F\u4F7F\u7528\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u5904\u7406\u7684\uFF0C\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u662F\u4E3A\u54CD\u5E94\u7279\u5B9A\u4E8B\u4EF6\uFF08\u4F8B\u5982\u6309\u94AE\u5355\u51FB\uFF09\u800C\u8C03\u7528\u7684\u51FD\u6570\u3002\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u4F5C\u4E3A props \u4ECE\u7236\u7EC4\u4EF6\u4F20\u9012\u5230\u5B50\u7EC4\u4EF6\uFF0C\u5E76\u5728\u5B50\u7EC4\u4EF6\u5185\u8C03\u7528\u3002</p><p>\u4F8B\u5982\uFF0C\u8981\u5904\u7406\u6309\u94AE\u70B9\u51FB\uFF0C\u60A8\u53EF\u4EE5\u5728\u7236\u7EC4\u4EF6\u4E2D\u5B9A\u4E49\u4E00\u4E2A\u4E8B\u4EF6\u5904\u7406\u51FD\u6570\uFF0C\u5E76\u5C06\u5176\u4F5C\u4E3A prop \u4F20\u9012\u7ED9\u5B50\u7EC4\u4EF6\u3002\u5355\u51FB\u6309\u94AE\u65F6\uFF0C\u5B50\u7EC4\u4EF6\u5C06\u8C03\u7528\u4E8B\u4EF6\u5904\u7406\u7A0B\u5E8F\u3002</p><h2 id="redux-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u4F5C\u7528\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#redux-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u4F5C\u7528\u662F\u4EC0\u4E48" aria-hidden="true">#</a> Redux \u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u4F5C\u7528\u662F\u4EC0\u4E48\uFF1F</h2><p>Redux \u662F\u4E00\u4E2A\u72B6\u6001\u7BA1\u7406\u5E93\uFF0C\u53EF\u4EE5\u4E0E React \u4E00\u8D77\u4F7F\u7528\u6765\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u7684\u5168\u5C40\u72B6\u6001\u3002</p><p>\u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\uFF0C\u6BCF\u4E2A\u7EC4\u4EF6\u90FD\u6709\u81EA\u5DF1\u7684\u672C\u5730\u72B6\u6001\uFF0C\u4F46\u901A\u5E38\u9700\u8981\u5728\u7EC4\u4EF6\u4E4B\u95F4\u5171\u4EAB\u6570\u636E\u3002Redux \u4E3A\u5E94\u7528\u7A0B\u5E8F\u7684\u5168\u5C40\u72B6\u6001\u63D0\u4F9B\u4E86\u4E00\u4E2A\u96C6\u4E2D\u5B58\u50A8\uFF0C\u4F7F\u5F97\u7EC4\u4EF6\u4E4B\u95F4\u7684\u6570\u636E\u7BA1\u7406\u548C\u5171\u4EAB\u53D8\u5F97\u66F4\u52A0\u5BB9\u6613\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-redux-\u4E2D-action-\u548C-reducers-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-redux-\u4E2D-action-\u548C-reducers-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B Redux \u4E2D action \u548C reducers \u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u5728 Redux \u4E2D\uFF0C\u64CD\u4F5C\u662F\u63CF\u8FF0\u4E8B\u4EF6\u6216\u5E94\u7528\u7A0B\u5E8F\u72B6\u6001\u66F4\u6539\u7684\u4FE1\u606F\u7684\u6709\u6548\u8D1F\u8F7D\u3002\u52A8\u4F5C\u88AB\u5206\u6D3E\u5230\u5546\u5E97\uFF0C\u5728\u90A3\u91CC\u5B83\u4EEC\u88AB\u4F20\u9012\u7ED9\u51CF\u901F\u5668\u3002</p><p>Reducers \u662F\u54CD\u5E94\u64CD\u4F5C\u66F4\u65B0\u5546\u5E97\u72B6\u6001\u7684\u51FD\u6570\u3002\u4ED6\u4EEC\u63A5\u6536\u5546\u5E97\u7684\u5F53\u524D\u72B6\u6001\u548C\u64CD\u4F5C\uFF0C\u5E76\u8FD4\u56DE\u53CD\u6620\u64CD\u4F5C\u63CF\u8FF0\u7684\u66F4\u6539\u7684\u65B0\u72B6\u6001\u3002</p><p>Actions \u548C reducer \u662F Redux \u7684\u6838\u5FC3\u6982\u5FF5\uFF0C\u5B83\u4EEC\u5141\u8BB8\u60A8\u4EE5\u53EF\u9884\u6D4B\u548C\u6709\u7EC4\u7EC7\u7684\u65B9\u5F0F\u7BA1\u7406\u548C\u66F4\u65B0\u5E94\u7528\u7A0B\u5E8F\u7684\u72B6\u6001\u3002</p><h2 id="\u4F60\u5982\u4F55\u6D4B\u8BD5-react-\u7EC4\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u4F60\u5982\u4F55\u6D4B\u8BD5-react-\u7EC4\u4EF6" aria-hidden="true">#</a> \u4F60\u5982\u4F55\u6D4B\u8BD5 React \u7EC4\u4EF6\uFF1F</h2><ul><li>\u6709\u5F88\u591A\u65B9\u6CD5\u53EF\u4EE5\u6D4B\u8BD5 React \u7EC4\u4EF6\uFF0C\u4F46\u4E00\u4E9B\u5E38\u89C1\u7684\u65B9\u6CD5\u5305\u62EC\uFF1A <ul><li>\u5355\u5143\u6D4B\u8BD5\uFF1A\u4F7F\u7528\u50CF Jest \u8FD9\u6837\u7684\u6D4B\u8BD5\u5E93\u5355\u72EC\u6D4B\u8BD5\u5355\u4E2A\u7EC4\u4EF6\u3002</li><li>\u5FEB\u7167\u6D4B\u8BD5\uFF1A\u62CD\u6444\u7EC4\u4EF6\u6E32\u67D3\u8F93\u51FA\u7684\u5FEB\u7167\u5E76\u5C06\u5176\u4E0E\u4E4B\u524D\u7684\u5FEB\u7167\u8FDB\u884C\u6BD4\u8F83\uFF0C\u4EE5\u786E\u4FDD\u5BF9\u7EC4\u4EF6\u6E32\u67D3\u8F93\u51FA\u7684\u66F4\u6539\u662F\u6709\u610F\u7684\u3002</li><li>\u7AEF\u5230\u7AEF\u6D4B\u8BD5\uFF1A\u5C06\u5E94\u7528\u7A0B\u5E8F\u4F5C\u4E3A\u4E00\u4E2A\u6574\u4F53\u8FDB\u884C\u6D4B\u8BD5\uFF0C\u6A21\u62DF\u7528\u6237\u4EA4\u4E92\u5E76\u786E\u4FDD\u5E94\u7528\u7A0B\u5E8F\u6309\u9884\u671F\u8FD0\u884C\u3002</li></ul></li></ul><p>\u6D4B\u8BD5\u662F\u6784\u5EFA React \u5E94\u7528\u7A0B\u5E8F\u7684\u91CD\u8981\u90E8\u5206\uFF0C\u56E0\u4E3A\u5B83\u53EF\u4EE5\u5E2E\u52A9\u60A8\u6355\u83B7\u9519\u8BEF\u5E76\u786E\u4FDD\u60A8\u7684\u5E94\u7528\u7A0B\u5E8F\u4FDD\u6301\u9AD8\u6027\u80FD\u548C\u53EF\u9760\u6027\u3002</p><h2 id="react-router-\u5728-react-\u5E94\u7528\u4E2D\u6709\u4EC0\u4E48\u7528" tabindex="-1"><a class="header-anchor" href="#react-router-\u5728-react-\u5E94\u7528\u4E2D\u6709\u4EC0\u4E48\u7528" aria-hidden="true">#</a> React Router \u5728 React \u5E94\u7528\u4E2D\u6709\u4EC0\u4E48\u7528\uFF1F</h2><p>React Router \u662F\u4E00\u4E2A\u7528\u4E8E\u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u8FDB\u884C\u8DEF\u7531\u7684\u5E93\u3002\u5B83\u63D0\u4F9B\u4E86\u4E00\u79CD\u5904\u7406\u5E94\u7528\u7A0B\u5E8F\u4E2D\u4E0D\u540C\u8DEF\u7531\u7684\u65B9\u6CD5\uFF0C\u4EE5\u4FBF\u7528\u6237\u53EF\u4EE5\u5728\u4E0D\u540C\u7684\u9875\u9762\u548C\u89C6\u56FE\u4E4B\u95F4\u5BFC\u822A\u3002</p><p>React Router \u4F7F\u7528\u8BF8\u5982 Route \u548C Link \u4E4B\u7C7B\u7684\u7EC4\u4EF6\u6765\u5B9A\u4E49\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u4E0D\u540C\u8DEF\u7531\u5E76\u5904\u7406\u5B83\u4EEC\u4E4B\u95F4\u7684\u5BFC\u822A\u3002</p><p>\u4F7F\u7528 React Router \u53EF\u4EE5\u8F7B\u677E\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7684\u8DEF\u7531\uFF0C\u5B83\u8FD8\u5141\u8BB8\u60A8\u6784\u5EFA\u5355\u9875\u5E94\u7528\u7A0B\u5E8F (SPA)\uFF0C\u5728\u5176\u4E2D\u5904\u7406\u9875\u9762\u4E4B\u95F4\u7684\u5BFC\u822A\u800C\u65E0\u9700\u91CD\u65B0\u52A0\u8F7D\u6574\u4E2A\u9875\u9762\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D-virtual-dom-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D-virtual-dom-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D Virtual DOM \u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u865A\u62DF DOM \u662F React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u5B9E\u9645 DOM\uFF08\u6587\u6863\u5BF9\u8C61\u6A21\u578B\uFF09\u7684 JavaScript \u8868\u793A\u3002\u865A\u62DF DOM \u5145\u5F53\u7EC4\u4EF6\u7684\u6E32\u67D3\u8F93\u51FA\u548C\u5B9E\u9645 DOM \u4E4B\u95F4\u7684\u4E2D\u4ECB\uFF0C\u5141\u8BB8 React \u5BF9 UI \u8FDB\u884C\u9AD8\u6548\u66F4\u65B0\u3002</p><p>\u5F53\u7EC4\u4EF6\u7684\u72B6\u6001\u6216 props \u53D1\u751F\u53D8\u5316\u65F6\uFF0CReact \u4F1A\u66F4\u65B0 Virtual DOM \u5E76\u8BA1\u7B97\u4E4B\u524D\u7684 Virtual DOM \u548C\u66F4\u65B0\u540E\u7684 Virtual DOM \u4E4B\u95F4\u7684\u5DEE\u5F02\u3002\u7136\u540E\uFF0CReact \u4EE5\u6700\u5C11\u7684\u5FC5\u8981\u66F4\u6539\u66F4\u65B0\u5B9E\u9645\u7684 DOM\uFF0C\u4F7F UI \u7684\u66F4\u65B0\u5C3D\u53EF\u80FD\u9AD8\u6548\u3002</p><p>\u865A\u62DF DOM \u7684\u4F7F\u7528\u662F\u4F7F React \u5982\u6B64\u5FEB\u901F\u548C\u9AD8\u6548\u7684\u5173\u952E\u7279\u6027\u4E4B\u4E00\uFF0C\u5B83\u4F7F React \u80FD\u591F\u5B9E\u65F6\u5904\u7406 UI \u66F4\u65B0\uFF0C\u5373\u4F7F\u5728\u590D\u6742\u548C\u5927\u578B\u5E94\u7528\u7A0B\u5E8F\u4E2D\u4E5F\u662F\u5982\u6B64\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u670D\u52A1\u5668\u7AEF\u6E32\u67D3-ssr-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u670D\u52A1\u5668\u7AEF\u6E32\u67D3-ssr-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u670D\u52A1\u5668\u7AEF\u6E32\u67D3\uFF08SSR\uFF09\u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u670D\u52A1\u5668\u7AEF\u6E32\u67D3 (SSR) \u662F\u4E00\u79CD\u5728\u670D\u52A1\u5668\u4E0A\u6E32\u67D3 React \u5E94\u7528\u7A0B\u5E8F\uFF0C\u7136\u540E\u5C06 HTML \u8F93\u51FA\u53D1\u9001\u5230\u5BA2\u6237\u7AEF\u7684\u6280\u672F\u3002\u8FD9\u4F7F\u5F97\u5E94\u7528\u7A0B\u5E8F\u7684\u521D\u59CB\u6E32\u67D3\u901F\u5EA6\u66F4\u5FEB\uFF0C\u56E0\u4E3A\u6D4F\u89C8\u5668\u4E0D\u5FC5\u5728\u663E\u793A\u5185\u5BB9\u4E4B\u524D\u7B49\u5F85 JavaScript \u52A0\u8F7D\u548C\u6267\u884C\u3002</p><p>SSR \u901A\u5E38\u7528\u4E8E\u63D0\u9AD8 React \u5E94\u7528\u7A0B\u5E8F\u7684\u6027\u80FD\uFF0C\u5E76\u786E\u4FDD\u641C\u7D22\u5F15\u64CE\u548C\u4E92\u8054\u7F51\u8FDE\u63A5\u901F\u5EA6\u6162\u6216\u4E0D\u53EF\u9760\u7684\u7528\u6237\u53EF\u4EE5\u8BBF\u95EE\u5E94\u7528\u7A0B\u5E8F\u7684\u5185\u5BB9\u3002</p><h2 id="react-hooks-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528" tabindex="-1"><a class="header-anchor" href="#react-hooks-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528" aria-hidden="true">#</a> React Hooks \u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528\uFF1F</h2><p>React Hooks \u662F\u4E00\u79CD\u5411\u529F\u80FD\u7EC4\u4EF6\u6DFB\u52A0\u72B6\u6001\u548C\u5176\u4ED6 React \u7279\u6027\u7684\u65B9\u6CD5\u3002\u5728 Hooks \u4E4B\u524D\uFF0C\u72B6\u6001\u548C\u5176\u4ED6 React \u7279\u6027\u53EA\u80FD\u6DFB\u52A0\u5230\u7C7B\u7EC4\u4EF6\u4E2D\u3002</p><p>React Hooks \u5141\u8BB8\u60A8\u5728\u7EC4\u4EF6\u4E4B\u95F4\u91CD\u7528\u903B\u8F91\uFF0C\u4ECE\u800C\u66F4\u8F7B\u677E\u5730\u5728\u5E94\u7528\u7A0B\u5E8F\u4E2D\u5171\u4EAB\u901A\u7528\u903B\u8F91\u3002\u5B83\u4EEC\u8FD8\u53EF\u4EE5\u66F4\u8F7B\u677E\u5730\u7F16\u5199\u53EF\u4EE5\u5904\u7406\u72B6\u6001\u548C\u5176\u4ED6 React \u529F\u80FD\u7684\u529F\u80FD\u7EC4\u4EF6\uFF0C\u4ECE\u800C\u4F7F\u60A8\u7684\u4EE3\u7801\u66F4\u6E05\u6670\u3001\u66F4\u6613\u8BFB\u3002</p><p>\u4E00\u4E9B\u5E38\u89C1\u7684\u94A9\u5B50\u5305\u62EC useState\u3001useEffect \u548C useContext\u3002</p><h2 id="react-context-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528" tabindex="-1"><a class="header-anchor" href="#react-context-\u5728-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528" aria-hidden="true">#</a> React Context \u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u6709\u4EC0\u4E48\u7528\uFF1F</h2><p>React Context \u662F\u4E00\u79CD\u5728\u7EC4\u4EF6\u4E4B\u95F4\u5171\u4EAB\u6570\u636E\u800C\u65E0\u9700\u901A\u8FC7\u7EC4\u4EF6\u6811\u7684\u591A\u4E2A\u7EA7\u522B\u5411\u4E0B\u4F20\u9012 props \u7684\u65B9\u6CD5\u3002</p><p>React Context \u63D0\u4F9B\u4E86\u4E00\u4E2A\u5168\u5C40\u6570\u636E\u5B58\u50A8\uFF0C\u53EF\u4EE5\u4ECE\u5E94\u7528\u7A0B\u5E8F\u7684\u4EFB\u4F55\u5730\u65B9\u8BBF\u95EE\uFF0C\u4F7F\u5F97\u7EC4\u4EF6\u4E4B\u95F4\u7684\u6570\u636E\u5171\u4EAB\u53D8\u5F97\u5BB9\u6613\u3002\u5B83\u901A\u5E38\u7528\u4E8E\u4E3B\u9898\u6570\u636E\u3001\u8BED\u8A00\u6570\u636E\u6216\u7528\u6237\u6570\u636E\u3002</p><p>React Context \u4F7F\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u7684\u5168\u5C40\u72B6\u6001\u53D8\u5F97\u5BB9\u6613\uFF0C\u5B83\u6709\u52A9\u4E8E\u4FDD\u6301\u4EE3\u7801\u7684\u7EC4\u7EC7\u6027\u5E76\u907F\u514D prop drilling\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u9AD8\u9636\u7EC4\u4EF6-hoc-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u4E2D\u9AD8\u9636\u7EC4\u4EF6-hoc-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u4E2D\u9AD8\u9636\u7EC4\u4EF6 (HOC) \u7684\u6982\u5FF5\u5417\uFF1F</h2><p>\u9AD8\u9636\u7EC4\u4EF6 (HOC) \u662F\u5C06\u7EC4\u4EF6\u4F5C\u4E3A\u53C2\u6570\u5E76\u8FD4\u56DE\u5177\u6709\u9644\u52A0\u5C5E\u6027\u6216\u884C\u4E3A\u7684\u65B0\u7EC4\u4EF6\u7684\u51FD\u6570\u3002</p><p>HOC \u901A\u5E38\u7528\u4E8E\u5411\u591A\u4E2A\u7EC4\u4EF6\u6DFB\u52A0\u901A\u7528\u903B\u8F91\u6216\u884C\u4E3A\uFF0C\u800C\u4E0D\u5FC5\u5728\u6BCF\u4E2A\u7EC4\u4EF6\u4E2D\u91CD\u590D\u903B\u8F91\u3002\u5B83\u4EEC\u5141\u8BB8\u60A8\u5C06\u903B\u8F91\u62BD\u8C61\u4E3A\u53EF\u91CD\u7528\u7684\u51FD\u6570\uFF0C\u4F7F\u60A8\u7684\u4EE3\u7801\u66F4\u6E05\u6670\u3001\u66F4\u6613\u8BFB\u3002</p><p>HOC \u662F React \u4E2D\u7684\u4E00\u4E2A\u5F3A\u5927\u5DE5\u5177\uFF0C\u5B83\u4EEC\u5141\u8BB8\u60A8\u4EE5\u7075\u6D3B\u4E14\u53EF\u6269\u5C55\u7684\u65B9\u5F0F\u5411\u7EC4\u4EF6\u6DFB\u52A0\u529F\u80FD\u3002</p><h2 id="\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D-react-portals-\u7684\u6982\u5FF5\u5417" tabindex="-1"><a class="header-anchor" href="#\u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B-react-\u5E94\u7528\u7A0B\u5E8F\u4E2D-react-portals-\u7684\u6982\u5FF5\u5417" aria-hidden="true">#</a> \u4F60\u80FD\u89E3\u91CA\u4E00\u4E0B React \u5E94\u7528\u7A0B\u5E8F\u4E2D React Portals \u7684\u6982\u5FF5\u5417\uFF1F</h2><p>React Portals \u662F\u4E00\u79CD\u5C06\u5B50\u7EC4\u4EF6\u6E32\u67D3\u5230\u7236\u7EC4\u4EF6 DOM \u6811\u5916\u90E8\u7684 DOM \u8282\u70B9\u7684\u65B9\u6CD5\u3002\u8FD9\u5141\u8BB8\u60A8\u5C06\u5B50\u6E32\u67D3\u5230 DOM \u7684\u4E0D\u540C\u90E8\u5206\uFF0C\u5373\u4F7F\u5B83\u5728\u7EC4\u4EF6\u7684\u4E3B DOM \u6811\u4E4B\u5916\u3002</p><p>React Portals \u901A\u5E38\u7528\u4E8E\u5728 React \u5E94\u7528\u7A0B\u5E8F\u4E2D\u5448\u73B0\u5BF9\u8BDD\u6846\u3001\u6A21\u5F0F\u6216\u5176\u4ED6\u7C7B\u578B\u7684\u5F39\u51FA\u7A97\u53E3\u3002\u901A\u8FC7\u4F7F\u7528 Portal\uFF0C\u60A8\u53EF\u4EE5\u5C06\u5F39\u51FA\u7A97\u53E3\u7684 UI \u4E0E\u7EC4\u4EF6\u7684\u4E3B UI \u5206\u5F00\uFF0C\u8FD9\u6709\u52A9\u4E8E\u4FDD\u6301\u4EE3\u7801\u7684\u7EC4\u7EC7\u6027\u5E76\u4F7F\u7BA1\u7406 UI \u53D8\u5F97\u66F4\u52A0\u5BB9\u6613\u3002</p><p>React Portals \u63D0\u4F9B\u4E86\u4E00\u79CD\u5F3A\u5927\u7684\u65B9\u6CD5\u6765\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u7684 UI\uFF0C\u5B83\u4EEC\u5141\u8BB8\u60A8\u5C06\u7EC4\u4EF6\u548C UI \u5206\u5F00\uFF0C\u4ECE\u800C\u4F7F\u60A8\u7684\u4EE3\u7801\u66F4\u6613\u4E8E\u7EF4\u62A4\u548C\u6D4B\u8BD5\u3002</p>',73),d=[p];function i(h,o){return e(),t("div",null,d)}const n=a(c,[["render",i],["__file","13.html.vue"]]);export{n as default};
