import{_ as n,o as s,c as a,a as t}from"./app.b7a23fca.js";const e={},p=t(`<h1 id="\u7F51\u7EDC\u901A\u4FE1tcp" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u901A\u4FE1tcp" aria-hidden="true">#</a> \u7F51\u7EDC\u901A\u4FE1TCP</h1><p>TCP\u534F\u8BAE\uFF0C\u4F20\u8F93\u63A7\u5236\u534F\u8BAE\uFF08\u82F1\u8BED\uFF1ATransmission Control Protocol\uFF0C\u7F29\u5199\u4E3A TCP\uFF09\u662F\u4E00\u79CD\u9762\u5411\u8FDE\u63A5\u7684\u3001\u53EF\u9760\u7684\u3001\u57FA\u4E8E\u5B57\u8282\u6D41\u7684\u4F20\u8F93\u5C42\u901A\u4FE1\u534F\u8BAE\uFF0C\u7531IETF\u7684RFC 793\u5B9A\u4E49\u3002</p><p>TCP\u901A\u4FE1\u9700\u8981\u7ECF\u8FC7\u521B\u5EFA\u8FDE\u63A5\u3001\u6570\u636E\u4F20\u9001\u3001\u7EC8\u6B62\u8FDE\u63A5\u4E09\u4E2A\u6B65\u9AA4\u3002</p><h2 id="\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a> \u7279\u70B9</h2><ul><li>\u9762\u5411\u8FDE\u63A5 \u901A\u4FE1\u53CC\u65B9\u5FC5\u987B\u5148\u5EFA\u7ACB\u8FDE\u63A5\u624D\u80FD\u8FDB\u884C\u6570\u636E\u7684\u4F20\u8F93\uFF0C\u53CC\u65B9\u90FD\u5FC5\u987B\u4E3A\u8BE5\u8FDE\u63A5\u5206\u914D\u5FC5\u8981\u7684\u7CFB\u7EDF\u5185\u6838\u8D44\u6E90\uFF0C\u4EE5\u7BA1\u7406\u8FDE\u63A5\u7684\u72B6\u6001\u548C\u8FDE\u63A5\u4E0A\u7684\u4F20\u8F93\u3002</li></ul><p>\u53CC\u65B9\u95F4\u7684\u6570\u636E\u4F20\u8F93\u90FD\u53EF\u4EE5\u901A\u8FC7\u8FD9\u4E00\u4E2A\u8FDE\u63A5\u8FDB\u884C\u3002</p><p>\u5B8C\u6210\u6570\u636E\u4EA4\u6362\u540E\uFF0C\u53CC\u65B9\u5FC5\u987B\u65AD\u5F00\u6B64\u8FDE\u63A5\uFF0C\u4EE5\u91CA\u653E\u7CFB\u7EDF\u8D44\u6E90\u3002</p><p>\u8FD9\u79CD\u8FDE\u63A5\u662F\u4E00\u5BF9\u4E00\u7684\uFF0C\u56E0\u6B64TCP\u4E0D\u9002\u7528\u4E8E\u5E7F\u64AD\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u57FA\u4E8E\u5E7F\u64AD\u7684\u5E94\u7528\u7A0B\u5E8F\u8BF7\u4F7F\u7528UDP\u534F\u8BAE\u3002</p><ul><li>\u53EF\u9760\u4F20\u8F93 1\uFF09TCP\u91C7\u7528\u53D1\u9001\u5E94\u7B54\u673A\u5236</li></ul><p>TCP\u53D1\u9001\u7684\u6BCF\u4E2A\u62A5\u6587\u6BB5\u90FD\u5FC5\u987B\u5F97\u5230\u63A5\u6536\u65B9\u7684\u5E94\u7B54\u624D\u8BA4\u4E3A\u8FD9\u4E2ATCP\u62A5\u6587\u6BB5\u4F20\u8F93\u6210\u529F</p><p>2\uFF09\u8D85\u65F6\u91CD\u4F20</p><p>\u53D1\u9001\u7AEF\u53D1\u51FA\u4E00\u4E2A\u62A5\u6587\u6BB5\u4E4B\u540E\u5C31\u542F\u52A8\u5B9A\u65F6\u5668\uFF0C\u5982\u679C\u5728\u5B9A\u65F6\u65F6\u95F4\u5185\u6CA1\u6709\u6536\u5230\u5E94\u7B54\u5C31\u91CD\u65B0\u53D1\u9001\u8FD9\u4E2A\u62A5\u6587\u6BB5\u3002</p><p>TCP\u4E3A\u4E86\u4FDD\u8BC1\u4E0D\u53D1\u751F\u4E22\u5305\uFF0C\u5C31\u7ED9\u6BCF\u4E2A\u5305\u4E00\u4E2A\u5E8F\u53F7\uFF0C\u540C\u65F6\u5E8F\u53F7\u4E5F\u4FDD\u8BC1\u4E86\u4F20\u9001\u5230\u63A5\u6536\u7AEF\u5B9E\u4F53\u7684\u5305\u7684\u6309\u5E8F\u63A5\u6536\u3002\u7136\u540E\u63A5\u6536\u7AEF\u5B9E\u4F53\u5BF9\u5DF2\u6210\u529F\u6536\u5230\u7684\u5305\u53D1\u56DE\u4E00\u4E2A\u76F8\u5E94\u7684\u786E\u8BA4\uFF08ACK\uFF09\uFF1B\u5982\u679C\u53D1\u9001\u7AEF\u5B9E\u4F53\u5728\u5408\u7406\u7684\u5F80\u8FD4\u65F6\u5EF6\uFF08RTT\uFF09\u5185\u672A\u6536\u5230\u786E\u8BA4\uFF0C\u90A3\u4E48\u5BF9\u5E94\u7684\u6570\u636E\u5305\u5C31\u88AB\u5047\u8BBE\u4E3A\u5DF2\u4E22\u5931\u5C06\u4F1A\u88AB\u8FDB\u884C\u91CD\u4F20\u3002</p><p>3\uFF09\u9519\u8BEF\u6821\u9A8C</p><p>TCP\u7528\u4E00\u4E2A\u6821\u9A8C\u548C\u51FD\u6570\u6765\u68C0\u9A8C\u6570\u636E\u662F\u5426\u6709\u9519\u8BEF\uFF1B\u5728\u53D1\u9001\u548C\u63A5\u6536\u65F6\u90FD\u8981\u8BA1\u7B97\u6821\u9A8C\u548C\u3002</p><ol start="4"><li>\u6D41\u91CF\u63A7\u5236\u548C\u963B\u585E\u7BA1\u7406</li></ol><p>\u6D41\u91CF\u63A7\u5236\u7528\u6765\u907F\u514D\u4E3B\u673A\u53D1\u9001\u5F97\u8FC7\u5FEB\u800C\u4F7F\u63A5\u6536\u65B9\u6765\u4E0D\u53CA\u5B8C\u5168\u6536\u4E0B\u3002</p><ul><li>TCP\u4E0EUDP\u7684\u4E0D\u540C\u70B9</li></ul><p>\u9762\u5411\u8FDE\u63A5\uFF08\u786E\u8BA4\u6709\u521B\u5EFA\u4E09\u65B9\u4EA4\u63E1\uFF0C\u8FDE\u63A5\u5DF2\u521B\u5EFA\u624D\u4F5C\u4F20\u8F93\u3002\uFF09 \u6709\u5E8F\u6570\u636E\u4F20\u8F93 \u91CD\u53D1\u4E22\u5931\u7684\u6570\u636E\u5305 \u820D\u5F03\u91CD\u590D\u7684\u6570\u636E\u5305 \u65E0\u5DEE\u9519\u7684\u6570\u636E\u4F20\u8F93 \u963B\u585E/\u6D41\u91CF\u63A7\u5236</p><ul><li>tcp\u6CE8\u610F\u70B9</li></ul><ol><li>tcp\u670D\u52A1\u5668\u4E00\u822C\u60C5\u51B5\u4E0B\u90FD\u9700\u8981\u7ED1\u5B9A\uFF0C\u5426\u5219\u5BA2\u6237\u7AEF\u627E\u4E0D\u5230\u8FD9\u4E2A\u670D\u52A1\u5668</li><li>tcp\u5BA2\u6237\u7AEF\u4E00\u822C\u4E0D\u7ED1\u5B9A\uFF0C\u56E0\u4E3A\u662F\u4E3B\u52A8\u94FE\u63A5\u670D\u52A1\u5668\uFF0C\u6240\u4EE5\u53EA\u8981\u786E\u5B9A\u597D\u670D\u52A1\u5668\u7684ip\u3001port\u7B49\u4FE1\u606F\u5C31\u597D\uFF0C\u672C\u5730\u5BA2\u6237\u7AEF\u53EF\u4EE5\u968F\u673A</li><li>tcp\u670D\u52A1\u5668\u4E2D\u901A\u8FC7listen\u53EF\u4EE5\u5C06socket\u521B\u5EFA\u51FA\u6765\u7684\u4E3B\u52A8\u5957\u63A5\u5B57\u53D8\u4E3A\u88AB\u52A8\u7684\uFF0C\u8FD9\u662F\u505Atcp\u670D\u52A1\u5668\u65F6\u5FC5\u987B\u8981\u505A\u7684</li><li>\u5F53\u5BA2\u6237\u7AEF\u9700\u8981\u94FE\u63A5\u670D\u52A1\u5668\u65F6\uFF0C\u5C31\u9700\u8981\u4F7F\u7528connect\u8FDB\u884C\u94FE\u63A5\uFF0Cudp\u662F\u4E0D\u9700\u8981\u94FE\u63A5\u7684\u800C\u662F\u76F4\u63A5\u53D1\u9001\uFF0C\u4F46\u662Ftcp\u5FC5\u987B\u5148\u94FE\u63A5\uFF0C\u53EA\u6709\u94FE\u63A5\u6210\u529F\u624D\u80FD\u901A\u4FE1</li><li>\u5F53\u4E00\u4E2Atcp\u5BA2\u6237\u7AEF\u8FDE\u63A5\u670D\u52A1\u5668\u65F6\uFF0C\u670D\u52A1\u5668\u7AEF\u4F1A\u67091\u4E2A\u65B0\u7684\u5957\u63A5\u5B57\uFF0C\u8FD9\u4E2A\u5957\u63A5\u5B57\u7528\u6765\u6807\u8BB0\u8FD9\u4E2A\u5BA2\u6237\u7AEF\uFF0C\u5355\u72EC\u4E3A\u8FD9\u4E2A\u5BA2\u6237\u7AEF\u670D\u52A1</li><li>listen\u540E\u7684\u5957\u63A5\u5B57\u662F\u88AB\u52A8\u5957\u63A5\u5B57\uFF0C\u7528\u6765\u63A5\u6536\u65B0\u7684\u5BA2\u6237\u7AEF\u7684\u94FE\u63A5\u8BF7\u6C42\u7684\uFF0C\u800Caccept\u8FD4\u56DE\u7684\u65B0\u5957\u63A5\u5B57\u662F\u6807\u8BB0\u8FD9\u4E2A\u65B0\u5BA2\u6237\u7AEF\u7684</li><li>\u5173\u95EDlisten\u540E\u7684\u5957\u63A5\u5B57\u610F\u5473\u7740\u88AB\u52A8\u5957\u63A5\u5B57\u5173\u95ED\u4E86\uFF0C\u4F1A\u5BFC\u81F4\u65B0\u7684\u5BA2\u6237\u7AEF\u4E0D\u80FD\u591F\u94FE\u63A5\u670D\u52A1\u5668\uFF0C\u4F46\u662F\u4E4B\u524D\u5DF2\u7ECF\u94FE\u63A5\u6210\u529F\u7684\u5BA2\u6237\u7AEF\u6B63\u5E38\u901A\u4FE1\u3002</li><li>\u5173\u95EDaccept\u8FD4\u56DE\u7684\u5957\u63A5\u5B57\u610F\u5473\u7740\u8FD9\u4E2A\u5BA2\u6237\u7AEF\u5DF2\u7ECF\u670D\u52A1\u5B8C\u6BD5</li><li>\u5F53\u5BA2\u6237\u7AEF\u7684\u5957\u63A5\u5B57\u8C03\u7528close\u540E\uFF0C\u670D\u52A1\u5668\u7AEF\u4F1Arecv\u89E3\u5835\u585E\uFF0C\u5E76\u4E14\u8FD4\u56DE\u7684\u957F\u5EA6\u4E3A0\uFF0C\u56E0\u6B64\u670D\u52A1\u5668\u53EF\u4EE5\u901A\u8FC7\u8FD4\u56DE\u6570\u636E\u7684\u957F\u5EA6\u6765\u533A\u522B\u5BA2\u6237\u7AEF\u662F\u5426\u5DF2\u7ECF\u4E0B\u7EBF</li></ol><ul><li>tcp\u76843\u6B21\u63E1\u624B,tcp\u76844\u6B21\u6325\u624B</li></ul><h2 id="tcp\u957F\u8FDE\u63A5\u548C\u77ED\u8FDE\u63A5" tabindex="-1"><a class="header-anchor" href="#tcp\u957F\u8FDE\u63A5\u548C\u77ED\u8FDE\u63A5" aria-hidden="true">#</a> tcp\u957F\u8FDE\u63A5\u548C\u77ED\u8FDE\u63A5</h2><p>TCP\u5728\u771F\u6B63\u7684\u8BFB\u5199\u64CD\u4F5C\u4E4B\u524D\uFF0Cserver\u4E0Eclient\u4E4B\u95F4\u5FC5\u987B\u5EFA\u7ACB\u4E00\u4E2A\u8FDE\u63A5\uFF0C\u5F53\u8BFB\u5199\u64CD\u4F5C\u5B8C\u6210\u540E\uFF0C\u53CC\u65B9\u4E0D\u518D\u9700\u8981\u8FD9\u4E2A\u8FDE\u63A5\u65F6\u5B83\u4EEC\u53EF\u4EE5\u91CA\u653E\u8FD9\u4E2A\u8FDE\u63A5\uFF0C\u8FDE\u63A5\u7684\u5EFA\u7ACB\u901A\u8FC7\u4E09\u6B21\u63E1\u624B\uFF0C\u91CA\u653E\u5219\u9700\u8981\u56DB\u6B21\u63E1\u624B\uFF0C\u6240\u4EE5\u8BF4\u6BCF\u4E2A\u8FDE\u63A5\u7684\u5EFA\u7ACB\u90FD\u662F\u9700\u8981\u8D44\u6E90\u6D88\u8017\u548C\u65F6\u95F4\u6D88\u8017\u7684\u3002</p><ul><li>TCP\u77ED\u8FDE\u63A5</li></ul><p>\u6A21\u62DF\u4E00\u79CDTCP\u77ED\u8FDE\u63A5\u7684\u60C5\u51B5:</p><ol><li>client \u5411 server \u53D1\u8D77\u8FDE\u63A5\u8BF7\u6C42</li><li>server \u63A5\u5230\u8BF7\u6C42\uFF0C\u53CC\u65B9\u5EFA\u7ACB\u8FDE\u63A5</li><li>client \u5411 server \u53D1\u9001\u6D88\u606F</li><li>server \u56DE\u5E94 client</li><li>\u4E00\u6B21\u8BFB\u5199\u5B8C\u6210\uFF0C\u6B64\u65F6\u53CC\u65B9\u4EFB\u4F55\u4E00\u4E2A\u90FD\u53EF\u4EE5\u53D1\u8D77 close \u64CD\u4F5C</li></ol><p>\u5728\u6B65\u9AA45\u4E2D\uFF0C\u4E00\u822C\u90FD\u662F client \u5148\u53D1\u8D77 close \u64CD\u4F5C\u3002\u5F53\u7136\u4E5F\u4E0D\u6392\u9664\u6709\u7279\u6B8A\u7684\u60C5\u51B5\u3002</p><p>\u4ECE\u4E0A\u9762\u7684\u63CF\u8FF0\u770B\uFF0C\u77ED\u8FDE\u63A5\u4E00\u822C\u53EA\u4F1A\u5728 client/server \u95F4\u4F20\u9012\u4E00\u6B21\u8BFB\u5199\u64CD\u4F5C\uFF01</p><ul><li>TCP\u957F\u8FDE\u63A5</li></ul><p>\u518D\u6A21\u62DF\u4E00\u79CD\u957F\u8FDE\u63A5\u7684\u60C5\u51B5:</p><ol><li>client \u5411 server \u53D1\u8D77\u8FDE\u63A5</li><li>server \u63A5\u5230\u8BF7\u6C42\uFF0C\u53CC\u65B9\u5EFA\u7ACB\u8FDE\u63A5</li><li>client \u5411 server \u53D1\u9001\u6D88\u606F</li><li>server \u56DE\u5E94 client</li><li>\u4E00\u6B21\u8BFB\u5199\u5B8C\u6210\uFF0C\u8FDE\u63A5\u4E0D\u5173\u95ED</li><li>\u540E\u7EED\u8BFB\u5199\u64CD\u4F5C...</li><li>\u957F\u65F6\u95F4\u64CD\u4F5C\u4E4B\u540Eclient\u53D1\u8D77\u5173\u95ED\u8BF7\u6C42</li></ol><ul><li>TCP\u957F/\u77ED\u8FDE\u63A5\u64CD\u4F5C\u8FC7\u7A0B</li></ul><blockquote><p>\u77ED\u8FDE\u63A5\u7684\u64CD\u4F5C\u6B65\u9AA4\u662F\uFF1A\u5EFA\u7ACB\u8FDE\u63A5\u2014\u2014\u6570\u636E\u4F20\u8F93\u2014\u2014\u5173\u95ED\u8FDE\u63A5...\u5EFA\u7ACB\u8FDE\u63A5\u2014\u2014\u6570\u636E\u4F20\u8F93\u2014\u2014\u5173\u95ED\u8FDE\u63A5</p></blockquote><blockquote><p>\u957F\u8FDE\u63A5\u7684\u64CD\u4F5C\u6B65\u9AA4\u662F\uFF1A\u5EFA\u7ACB\u8FDE\u63A5\u2014\u2014\u6570\u636E\u4F20\u8F93...\uFF08\u4FDD\u6301\u8FDE\u63A5\uFF09...\u6570\u636E\u4F20\u8F93\u2014\u2014\u5173\u95ED\u8FDE\u63A5</p></blockquote><ul><li>TCP\u957F/\u77ED\u8FDE\u63A5\u7684\u4F18\u70B9\u548C\u7F3A\u70B9</li></ul><p>\u957F\u8FDE\u63A5\u53EF\u4EE5\u7701\u53BB\u8F83\u591A\u7684TCP\u5EFA\u7ACB\u548C\u5173\u95ED\u7684\u64CD\u4F5C\uFF0C\u51CF\u5C11\u6D6A\u8D39\uFF0C\u8282\u7EA6\u65F6\u95F4\u3002</p><p>\u5BF9\u4E8E\u9891\u7E41\u8BF7\u6C42\u8D44\u6E90\u7684\u5BA2\u6237\u6765\u8BF4\uFF0C\u8F83\u9002\u7528\u957F\u8FDE\u63A5\u3002</p><p>client\u4E0Eserver\u4E4B\u95F4\u7684\u8FDE\u63A5\u5982\u679C\u4E00\u76F4\u4E0D\u5173\u95ED\u7684\u8BDD\uFF0C\u4F1A\u5B58\u5728\u4E00\u4E2A\u95EE\u9898\uFF0C</p><p>\u968F\u7740\u5BA2\u6237\u7AEF\u8FDE\u63A5\u8D8A\u6765\u8D8A\u591A\uFF0Cserver\u65E9\u665A\u6709\u625B\u4E0D\u4F4F\u7684\u65F6\u5019\uFF0C\u8FD9\u65F6\u5019server\u7AEF\u9700\u8981\u91C7\u53D6\u4E00\u4E9B\u7B56\u7565\uFF0C</p><p>\u5982\u5173\u95ED\u4E00\u4E9B\u957F\u65F6\u95F4\u6CA1\u6709\u8BFB\u5199\u4E8B\u4EF6\u53D1\u751F\u7684\u8FDE\u63A5\uFF0C\u8FD9\u6837\u53EF\u4EE5\u907F\u514D\u4E00\u4E9B\u6076\u610F\u8FDE\u63A5\u5BFC\u81F4server\u7AEF\u670D\u52A1\u53D7\u635F\uFF1B</p><p>\u5982\u679C\u6761\u4EF6\u518D\u5141\u8BB8\u5C31\u53EF\u4EE5\u4EE5\u5BA2\u6237\u7AEF\u673A\u5668\u4E3A\u9897\u7C92\u5EA6\uFF0C\u9650\u5236\u6BCF\u4E2A\u5BA2\u6237\u7AEF\u7684\u6700\u5927\u957F\u8FDE\u63A5\u6570\uFF0C</p><p>\u8FD9\u6837\u53EF\u4EE5\u5B8C\u5168\u907F\u514D\u67D0\u4E2A\u86CB\u75BC\u7684\u5BA2\u6237\u7AEF\u8FDE\u7D2F\u540E\u7AEF\u670D\u52A1\u3002</p><p>\u77ED\u8FDE\u63A5\u5BF9\u4E8E\u670D\u52A1\u5668\u6765\u8BF4\u7BA1\u7406\u8F83\u4E3A\u7B80\u5355\uFF0C\u5B58\u5728\u7684\u8FDE\u63A5\u90FD\u662F\u6709\u7528\u7684\u8FDE\u63A5\uFF0C\u4E0D\u9700\u8981\u989D\u5916\u7684\u63A7\u5236\u624B\u6BB5\u3002 \u4F46\u5982\u679C\u5BA2\u6237\u8BF7\u6C42\u9891\u7E41\uFF0C\u5C06\u5728TCP\u7684\u5EFA\u7ACB\u548C\u5173\u95ED\u64CD\u4F5C\u4E0A\u6D6A\u8D39\u65F6\u95F4\u548C\u5E26\u5BBD\u3002</p><ul><li>TCP\u957F/\u77ED\u8FDE\u63A5\u7684\u5E94\u7528\u573A\u666F</li></ul><p>\u957F\u8FDE\u63A5\u591A\u7528\u4E8E\u64CD\u4F5C\u9891\u7E41\uFF0C\u70B9\u5BF9\u70B9\u7684\u901A\u8BAF\uFF0C\u800C\u4E14\u8FDE\u63A5\u6570\u4E0D\u80FD\u592A\u591A\u60C5\u51B5\u3002\u6BCF\u4E2ATCP\u8FDE\u63A5\u90FD\u9700\u8981\u4E09\u6B21\u63E1\u624B\uFF0C\u8FD9\u9700\u8981\u65F6\u95F4\uFF0C\u5982\u679C\u6BCF\u4E2A\u64CD\u4F5C\u90FD\u662F\u5148\u8FDE\u63A5\uFF0C\u518D\u64CD\u4F5C\u7684\u8BDD\u90A3\u4E48\u5904\u7406\u901F\u5EA6\u4F1A\u964D\u4F4E\u5F88\u591A\uFF0C\u6240\u4EE5\u6BCF\u4E2A\u64CD\u4F5C\u5B8C\u540E\u90FD\u4E0D\u65AD\u5F00\uFF0C\u518D\u6B21\u5904\u7406\u65F6\u76F4\u63A5\u53D1\u9001\u6570\u636E\u5305\u5C31OK\u4E86\uFF0C\u4E0D\u7528\u5EFA\u7ACBTCP\u8FDE\u63A5\u3002\u4F8B\u5982\uFF1A\u6570\u636E\u5E93\u7684\u8FDE\u63A5\u7528\u957F\u8FDE\u63A5\uFF0C\u5982\u679C\u7528\u77ED\u8FDE\u63A5\u9891\u7E41\u7684\u901A\u4FE1\u4F1A\u9020\u6210socket\u9519\u8BEF\uFF0C\u800C\u4E14\u9891\u7E41\u7684socket \u521B\u5EFA\u4E5F\u662F\u5BF9\u8D44\u6E90\u7684\u6D6A\u8D39\u3002</p><p>\u800C\u50CFWEB\u7F51\u7AD9\u7684http\u670D\u52A1\u4E00\u822C\u90FD\u7528\u77ED\u94FE\u63A5\uFF0C\u56E0\u4E3A\u957F\u8FDE\u63A5\u5BF9\u4E8E\u670D\u52A1\u7AEF\u6765\u8BF4\u4F1A\u8017\u8D39\u4E00\u5B9A\u7684\u8D44\u6E90\uFF0C\u800C\u50CFWEB\u7F51\u7AD9\u8FD9\u4E48\u9891\u7E41\u7684\u6210\u5343\u4E0A\u4E07\u751A\u81F3\u4E0A\u4EBF\u5BA2\u6237\u7AEF\u7684\u8FDE\u63A5\u7528\u77ED\u8FDE\u63A5\u4F1A\u66F4\u7701\u4E00\u4E9B\u8D44\u6E90\uFF0C\u5982\u679C\u7528\u957F\u8FDE\u63A5\uFF0C\u800C\u4E14\u540C\u65F6\u6709\u6210\u5343\u4E0A\u4E07\u7684\u7528\u6237\uFF0C\u5982\u679C\u6BCF\u4E2A\u7528\u6237\u90FD\u5360\u7528\u4E00\u4E2A\u8FDE\u63A5\u7684\u8BDD\uFF0C\u90A3\u53EF\u60F3\u800C\u77E5\u5427\u3002\u6240\u4EE5\u5E76\u53D1\u91CF\u5927\uFF0C\u4F46\u6BCF\u4E2A\u7528\u6237\u65E0\u9700\u9891\u7E41\u64CD\u4F5C\u60C5\u51B5\u4E0B\u9700\u7528\u77ED\u8FDE\u597D\u3002</p><h2 id="\u4EE3\u7801" tabindex="-1"><a class="header-anchor" href="#\u4EE3\u7801" aria-hidden="true">#</a> \u4EE3\u7801</h2><ul><li>\u5BA2\u670D\u7AEF</li></ul><div class="language-python ext-py line-numbers-mode"><pre class="language-python"><code><span class="token keyword">import</span> socket

<span class="token keyword">def</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token comment"># \u521B\u5EFA\u5957\u63A5\u5B57</span>
    tcp_socket <span class="token operator">=</span> socket<span class="token punctuation">.</span>socket<span class="token punctuation">(</span>socket<span class="token punctuation">.</span>AF_INET<span class="token punctuation">,</span> socket<span class="token punctuation">.</span>SOCK_STREAM<span class="token punctuation">)</span>

    <span class="token comment"># \u8FDE\u63A5\u670D\u52A1\u5668</span>
    server_ip <span class="token operator">=</span> <span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u8981\u94FE\u63A5\u7684\u670D\u52A1\u5668\u7684ip:&#39;</span><span class="token punctuation">)</span>
    server_port <span class="token operator">=</span> <span class="token builtin">int</span><span class="token punctuation">(</span><span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u8981\u8FDE\u63A5\u7684\u670D\u52A1\u5668\u7684port:&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
    server_addr <span class="token operator">=</span> <span class="token punctuation">(</span>server_ip<span class="token punctuation">,</span> server_port<span class="token punctuation">)</span>
    tcp_socket<span class="token punctuation">.</span>connect<span class="token punctuation">(</span>server_addr<span class="token punctuation">)</span>

    <span class="token comment"># \u53D1\u9001/\u63A5\u6536\u6570\u636E</span>
    send_data <span class="token operator">=</span> <span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u53D1\u9001\u7684\u6570\u636E:&#39;</span><span class="token punctuation">)</span>
    tcp_socket<span class="token punctuation">.</span>send<span class="token punctuation">(</span>send_data<span class="token punctuation">.</span>encode<span class="token punctuation">(</span><span class="token string">&#39;utf-8&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u5173\u95ED\u5957\u63A5\u5B57</span>


<span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&#39;__main__&#39;</span><span class="token punctuation">:</span>
    main<span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>\u670D\u52A1\u7AEF</li></ul><div class="language-python ext-py line-numbers-mode"><pre class="language-python"><code><span class="token keyword">import</span> socket


<span class="token keyword">def</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token comment"># \u4E70\u4E2A\u624B\u673A\uFF08\u521B\u5EFA\u5957\u63A5\u5B57\uFF09</span>
    tcp_sever_socket <span class="token operator">=</span> socket<span class="token punctuation">.</span>socket<span class="token punctuation">(</span>socket<span class="token punctuation">.</span>AF_INET<span class="token punctuation">,</span> socket<span class="token punctuation">.</span>SOCK_STREAM<span class="token punctuation">)</span>

    <span class="token comment"># \u63D2\u4E2A\u624B\u673A\u5361\uFF08\u7ED1\u5B9A\u672C\u5730\u4FE1\u606F\uFF09</span>
    tcp_sever_socket<span class="token punctuation">.</span>bind<span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token string">&#39;&#39;</span><span class="token punctuation">,</span> <span class="token number">7890</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u624B\u673A\u8BBE\u7F6E\u6B63\u5E38\u54CD\u94C3\u6A21\u5F0F\uFF08\u9ED8\u8BA4\u7684\u5957\u63A5\u5B57\u6709\u4E3B\u52A8\u53D8\u88AB\u52A8 listen\uFF09</span>
    tcp_sever_socket<span class="token punctuation">.</span>listen<span class="token punctuation">(</span><span class="token number">128</span><span class="token punctuation">)</span>

    <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&#39;------1--------&#39;</span><span class="token punctuation">)</span>
    <span class="token comment"># \u7B49\u5F85\u522B\u4EBA\u7684\u7535\u8BDD\u5230\u6765\uFF08\u7B49\u5F85\u5BA2\u670D\u7AEF\u8FDE\u63A5 accept\uFF09</span>
    new_client_socket<span class="token punctuation">,</span> client_addr <span class="token operator">=</span> tcp_sever_socket<span class="token punctuation">.</span>accept<span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&#39;------2--------&#39;</span><span class="token punctuation">)</span>
    <span class="token keyword">print</span><span class="token punctuation">(</span>client_addr<span class="token punctuation">)</span>

    <span class="token comment"># \u63A5\u6536\u5BA2\u670D\u7AEF\u53D1\u9001\u8FC7\u6765\u7684\u8BF7\u6C42</span>
    recv_data <span class="token operator">=</span> new_client_socket<span class="token punctuation">.</span>recv<span class="token punctuation">(</span><span class="token number">1024</span><span class="token punctuation">)</span>
    <span class="token keyword">print</span><span class="token punctuation">(</span>recv_data<span class="token punctuation">.</span>decode<span class="token punctuation">(</span><span class="token string">&#39;utf-8&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u56DE\u9001\u4E00\u90E8\u5206\u6570\u636E\u7ED9\u5BA2\u670D\u7AEF</span>
    <span class="token comment"># new_client_socket.send(&#39;haha&#39;.encode(&#39;utf-8&#39;))</span>
    new_client_socket<span class="token punctuation">.</span>send<span class="token punctuation">(</span><span class="token string">&#39;\u4F60\u54C8\u554A&#39;</span><span class="token punctuation">.</span>encode<span class="token punctuation">(</span><span class="token string">&#39;utf-8&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u5173\u95ED\u5957\u63A5\u5B57</span>
    new_client_socket<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>
    tcp_sever_socket<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>


<span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&#39;__main__&#39;</span><span class="token punctuation">:</span>
    main<span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>\u6587\u4EF6\u4E0B\u8F7D\u5BA2\u670D\u7AEF</li></ul><div class="language-python ext-py line-numbers-mode"><pre class="language-python"><code><span class="token keyword">import</span> socket


<span class="token keyword">def</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token comment"># \u521B\u5EFA\u5957\u63A5\u5B57</span>
    tcp_socket <span class="token operator">=</span> socket<span class="token punctuation">.</span>socket<span class="token punctuation">(</span>socket<span class="token punctuation">.</span>AF_INET<span class="token punctuation">,</span> socket<span class="token punctuation">.</span>SOCK_STREAM<span class="token punctuation">)</span>

    <span class="token comment"># \u83B7\u53D6\u670D\u52A1\u5668\u7684ip port</span>
    dest_ip <span class="token operator">=</span> <span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u4E0B\u8F7D\u670D\u52A1\u5668\u7684ip\uFF1A&#39;</span><span class="token punctuation">)</span>
    dest_port <span class="token operator">=</span> <span class="token builtin">int</span><span class="token punctuation">(</span><span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u4E0B\u8F7D\u670D\u52A1\u5668\u7684port\uFF1A&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u8FDE\u63A5\u670D\u52A1\u5668</span>
    tcp_socket<span class="token punctuation">.</span>connect<span class="token punctuation">(</span><span class="token punctuation">(</span>dest_ip<span class="token punctuation">,</span> dest_port<span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u83B7\u53D6\u4E0B\u8F7D\u7684\u6587\u4EF6\u540D\u5B57</span>
    download_file_name <span class="token operator">=</span> <span class="token builtin">input</span><span class="token punctuation">(</span><span class="token string">&#39;\u8BF7\u8F93\u5165\u8981\u4E0B\u8F7D\u7684\u6587\u4EF6\u540D\uFF1A&#39;</span><span class="token punctuation">)</span>

    <span class="token comment"># \u5C06\u6587\u4EF6\u540D\u5B57\u53D1\u9001\u5230\u670D\u52A1\u5668</span>
    tcp_socket<span class="token punctuation">.</span>send<span class="token punctuation">(</span>download_file_name<span class="token punctuation">.</span>encode<span class="token punctuation">(</span><span class="token string">&#39;utf-8&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u63A5\u6536\u6587\u4EF6\u4E2D\u7684\u6570\u636E</span>
    recv_data <span class="token operator">=</span> tcp_socket<span class="token punctuation">.</span>recv<span class="token punctuation">(</span><span class="token number">1024</span><span class="token punctuation">)</span>  <span class="token comment"># 1024 ---&gt; 1k 1024 * 1024 ---&gt; 1mb</span>

    <span class="token keyword">if</span> recv_data<span class="token punctuation">:</span>
        <span class="token comment"># \u4FDD\u5B58\u63A5\u6536\u5230\u7684\u6570\u636E\u5230\u4E00\u4E2A\u6587\u4EF6\u4E2D</span>
        <span class="token keyword">with</span> <span class="token builtin">open</span><span class="token punctuation">(</span><span class="token string">&#39;[\u65B0]&#39;</span> <span class="token operator">+</span> download_file_name<span class="token punctuation">,</span> <span class="token string">&#39;wb&#39;</span><span class="token punctuation">)</span> <span class="token keyword">as</span> f<span class="token punctuation">:</span>
            f<span class="token punctuation">.</span>write<span class="token punctuation">(</span>recv_data<span class="token punctuation">)</span>

    <span class="token comment"># \u5173\u95ED\u5957\u63A5\u5B57</span>
    tcp_socket<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>


<span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&#39;__main__&#39;</span><span class="token punctuation">:</span>
    main<span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><ul><li>\u6587\u4EF6\u4E0B\u8F7D\u670D\u52A1\u7AEF</li></ul><div class="language-python ext-py line-numbers-mode"><pre class="language-python"><code><span class="token keyword">import</span> socket


<span class="token keyword">def</span> <span class="token function">send_file_2_client</span><span class="token punctuation">(</span>new_client_socket<span class="token punctuation">,</span> client_addr<span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token triple-quoted-string string">&quot;&quot;&quot; \u53D1\u9001\u6587\u4EF6\u7ED9\u5BA2\u6237\u7AEF &quot;&quot;&quot;</span>
    <span class="token comment"># \u63A5\u6536\u5BA2\u670D\u7AEF\u53D1\u9001\u8FC7\u6765\u7684 \u8981\u4E0B\u8F7D\u7684\u6587\u4EF6\u540D</span>
    file_name <span class="token operator">=</span> new_client_socket<span class="token punctuation">.</span>recv<span class="token punctuation">(</span><span class="token number">1024</span><span class="token punctuation">)</span><span class="token punctuation">.</span>decode<span class="token punctuation">(</span><span class="token string">&#39;utf-8&#39;</span><span class="token punctuation">)</span>
    <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&#39;\u5BA2\u670D\u7AEF(%s)\u9700\u8981\u4E0B\u8F7D\u7684\u6587\u4EF6\u662F\uFF1A%s&#39;</span> <span class="token operator">%</span> <span class="token punctuation">(</span><span class="token builtin">str</span><span class="token punctuation">(</span>client_addr<span class="token punctuation">)</span><span class="token punctuation">,</span> file_name<span class="token punctuation">)</span><span class="token punctuation">)</span>

    file_content <span class="token operator">=</span> <span class="token boolean">None</span>
    <span class="token comment"># \u6253\u5F00\u6587\u4EF6\uFF0C\u8BFB\u53D6\u6570\u636E</span>
    <span class="token keyword">try</span><span class="token punctuation">:</span>
        f <span class="token operator">=</span> <span class="token builtin">open</span><span class="token punctuation">(</span>file_name<span class="token punctuation">,</span> <span class="token string">&#39;rb&#39;</span><span class="token punctuation">)</span>
        file_content <span class="token operator">=</span> f<span class="token punctuation">.</span>read<span class="token punctuation">(</span><span class="token punctuation">)</span>
        f<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>
    <span class="token keyword">except</span> Exception <span class="token keyword">as</span> ret<span class="token punctuation">:</span>
        <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">&#39;\u6CA1\u6709\u8981\u4E0B\u8F7D\u7684\u6587\u4EF6\uFF08%s\uFF09&#39;</span> <span class="token operator">%</span> file_name<span class="token punctuation">)</span>

    <span class="token comment"># \u53D1\u9001\u6587\u4EF6\u6570\u636E\u7ED9\u5BA2\u6237\u7AEF</span>
    <span class="token keyword">if</span> file_content<span class="token punctuation">:</span>
        <span class="token comment"># new_client_socket.send(&#39;\u4F60\u54C8\u554A&#39;.encode(&#39;utf-8&#39;))</span>
        new_client_socket<span class="token punctuation">.</span>send<span class="token punctuation">(</span>file_content<span class="token punctuation">)</span>


<span class="token keyword">def</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token comment"># \u4E70\u4E2A\u624B\u673A\uFF08\u521B\u5EFA\u5957\u63A5\u5B57\uFF09</span>
    tcp_sever_socket <span class="token operator">=</span> socket<span class="token punctuation">.</span>socket<span class="token punctuation">(</span>socket<span class="token punctuation">.</span>AF_INET<span class="token punctuation">,</span> socket<span class="token punctuation">.</span>SOCK_STREAM<span class="token punctuation">)</span>

    <span class="token comment"># \u63D2\u4E2A\u624B\u673A\u5361\uFF08\u7ED1\u5B9A\u672C\u5730\u4FE1\u606F\uFF09</span>
    tcp_sever_socket<span class="token punctuation">.</span>bind<span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token string">&#39;&#39;</span><span class="token punctuation">,</span> <span class="token number">7890</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

    <span class="token comment"># \u624B\u673A\u8BBE\u7F6E\u6B63\u5E38\u54CD\u94C3\u6A21\u5F0F\uFF08\u9ED8\u8BA4\u7684\u5957\u63A5\u5B57\u6709\u4E3B\u52A8\u53D8\u88AB\u52A8 listen\uFF09</span>
    tcp_sever_socket<span class="token punctuation">.</span>listen<span class="token punctuation">(</span><span class="token number">128</span><span class="token punctuation">)</span>

    <span class="token keyword">while</span> <span class="token boolean">True</span><span class="token punctuation">:</span>
        <span class="token comment"># \u7B49\u5F85\u522B\u4EBA\u7684\u7535\u8BDD\u5230\u6765\uFF08\u7B49\u5F85\u5BA2\u670D\u7AEF\u8FDE\u63A5 accept\uFF09</span>
        new_client_socket<span class="token punctuation">,</span> client_addr <span class="token operator">=</span> tcp_sever_socket<span class="token punctuation">.</span>accept<span class="token punctuation">(</span><span class="token punctuation">)</span>

        <span class="token comment"># \u8C03\u7528\u53D1\u9001\u6587\u4EF6\u51FD\u6570\uFF0C\u5B8C\u6210\u5BA2\u6237\u7AEF\u670D\u52A1</span>
        send_file_2_client<span class="token punctuation">(</span>new_client_socket<span class="token punctuation">,</span> client_addr<span class="token punctuation">)</span>

        <span class="token comment"># \u5173\u95ED\u5957\u63A5\u5B57</span>
        new_client_socket<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>

    tcp_sever_socket<span class="token punctuation">.</span>close<span class="token punctuation">(</span><span class="token punctuation">)</span>


<span class="token keyword">if</span> __name__ <span class="token operator">==</span> <span class="token string">&#39;__main__&#39;</span><span class="token punctuation">:</span>
    main<span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>`,56),c=[p];function i(l,o){return s(),a("div",null,c)}const d=n(e,[["render",i],["__file","tcp.html.vue"]]);export{d as default};
